INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1401301, 'Dabrafenib', 'Lonafarnib', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of lonafarnib. In vitro, lonafarnib has been shown to be primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 1A2, 2A6, 2C8, 2C9, 2C19, and 2E1.', 'DDInter', 1767317476, 'Concomitant use of lonafarnib with potent or moderate CYP450 3A4 inducers is considered contraindicated.', 'Metabolism', 'Concomitant use of lonafarnib with potent or moderate CYP450 3A4 inducers is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1401302, 'Dabrafenib', 'Lorlatinib', 'Major', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of lorlatinib, which has been found to be metabolized primarily by CYP450 3A4 and UGT1A4 in vitro. In addition to the potential for diminished efficacy of lorlatinib, the risk of serious hepatotoxicity may be increased due to activation of the pregnane X receptor (PXR) by both lorlatinib and CYP450 3A4 inducers. The effect of concomitant use of moderate CYP450 3A4 inducers on lorlatinib pharmacokinetics or the risk of hepatotoxicity is unknown.', 'DDInter', 1767317476, 'Concomitant use of lorlatinib with moderate CYP450 3A4 inducers should generally be avoided. If coadministration is required, monitoring of AST, ALT and bilirubin is recommended 48 hours after initiating lorlatinib and at least 3 times during the first week of treatment. Depending upon the relative importance of each drug, lorlatinib or the CYP450 3A4 inducer should be discontinued for persistent Grade 2 or higher hepatotoxicity.', 'Metabolism', 'Concomitant use of lorlatinib with moderate CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1401303, 'Dabrafenib', 'Losartan', 'Moderate', 'Coadministration with dabrafenib may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4 and/or 2C9. Dabrafenib has been found in vitro to be an inducer of these isoenzymes.', 'DDInter', 1767317476, 'Caution is advised when dabrafenib is prescribed with drugs that undergo metabolism by CYP450 3A4 and/or 2C9. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever dabrafenib is added to or withdrawn from therapy. Significantly reduced plasma concentrations and loss of efficacy may occur with sensitive substrates of CYP450 3A4 or 2C9 such as hormonal contraceptives, immunosuppressants (cyclosporine, everolimus, sirolimus, tacrolimus), ivacaftor, and warfarin-type anticoagulants. Alternatives to these medications should be considered if possible.', 'Metabolism', 'Caution is advised when dabrafenib is prescribed with drugs that undergo metabolism by CYP450 3A4 and/or 2C9.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1401304, 'Dabrafenib', 'Lovastatin', 'Moderate', 'Coadministration with dabrafenib may alter the pharmacokinetics of HMG-CoA reductase inhibitors. Dabrafenib has been found in vitro to be an inhibitor of organic anion transporting polypeptide (OATP) 1B1 and 1B3. Since many statins including atorvastatin, fluvastatin, pitavastatin, pravastatin, rosuvastatin, and simvastatin acid (active metabolite of simvastatin) are thought to be substrates of the hepatic uptake transporters, increased plasma concentrations may be expected during coadministration with dabrafenib, although this has not been studied. On the other hand, dabrafenib has also been reported to be an inducer of CYP450 3A4 and 2C9 in vivo. Reduced plasma concentrations may occur for substrates of these isoenzymes such as atorvastatin, fluvastatin, lovastatin, and simvastatin.', 'DDInter', 1767317476, 'Until more information is available, caution is advised when dabrafenib is prescribed with HMG-Coa reductase inhibitors. Clinical and laboratory monitoring are recommended whenever dabrafenib is added to or withdrawn from therapy, and the statin dosage adjusted as necessary. All patients treated with HMG-CoA reductase inhibitors should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'Until more information is available, caution is advised when dabrafenib is prescribed with HMG-Coa reductase inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1401305, 'Dabrafenib', 'Lumateperone', 'Major', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of lumateperone, which is metabolized by the isoenzyme in vitro.', 'DDInter', 1767317476, 'Coadministration of CYP450 3A4 inducers with lumateperone should be avoided.', 'Metabolism', 'Coadministration of CYP450 3A4 inducers with lumateperone should be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
