INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1401307, 'Dabrafenib', 'Lurbinectedin', 'Major', 'Coadministration with a strong or moderate CYP450 3A4 inducer is predicted to decrease lurbinectedin systemic exposure, which may reduce its efficacy.', 'DDInter', 1767317476, 'Concomitant use of lurbinectedin and strong or moderate CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'Concomitant use of lurbinectedin and strong or moderate CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1401308, 'Dabrafenib', 'Macimorelin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of macimorelin, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'The prescribing information for macimorelin recommends discontinuing potent CYP450 3A4 inducers (e.g., carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, rifamycins, St. John''s wort) as well as some moderate ones (e.g., bosentan, efavirenz, etravirine, modafinil) prior to macimorelin administration. A sufficient washout period following discontinuation of the inducers is also advised before using macimorelin. No recommendations are available for other, less potent CYP450 3A4 inducers; however, it may be appropriate to follow the same precaution.', 'Metabolism', 'The prescribing information for macimorelin recommends discontinuing potent CYP450 3A4 inducers (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1401309, 'Dabrafenib', 'Magaldrate', 'Moderate', 'Drugs that alter the pH of the upper gastrointestinal tract may affect the solubility of dabrafenib and reduce its bioavailability. The interaction has not been formally studied with H2-receptor antagonists or antacids; however, the solubility of dabrafenib is known to be pH-dependent. It has been reported to be very slightly soluble at pH 1 and practically insoluble above pH 4 in aqueous media.', 'DDInter', 1767317476, 'Until more information is available, caution is advised if dabrafenib is used in combination with H2-receptor antagonists or antacids. The potential for diminished therapeutic effects of dabrafenib should be considered, and pharmacologic response should be closely monitored.', 'Absorption', 'Until more information is available, caution is advised if dabrafenib is used in combination with H2-receptor antagonists or antacids.', NULL, NULL, 'Moderate', 'pharmacodynamic');
