INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1401319, 'Dabrafenib', 'Methadone', 'Major', 'Coadministration with inducers of various CYP450 isoenzymes may decrease the plasma concentrations of methadone, which is metabolized by CYP450 3A4, 2B6, 2C19, 2C9, and 2D6. Reduced analgesic efficacy or withdrawal symptoms may occur in patients maintained on methadone following the addition of an inducer. Conversely, discontinuation of the inducer may increase methadone plasma concentrations and potentiate the risk of overdose and fatal respiratory depression.', 'DDInter', 1767317476, 'Caution is advised if methadone is prescribed with CYP450 2B6, 2C19, 2C9 and/or 3A4 inducers. Pharmacologic response to methadone should be monitored more closely whenever an inducer is added to or withdrawn from therapy, and the dosage adjusted as necessary.', 'Metabolism', 'Caution is advised if methadone is prescribed with CYP450 2B6, 2C19, 2C9 and/or 3A4 inducers.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1401320, 'Dabrafenib', 'Methoxsalen', 'Moderate', 'Concomitant use of methoxsalen with other known photosensitizing agents may increase the risk of photosensitivity reactions. These agents include anthralin, coal tar or coal tar derivatives, griseofulvin, hypericin extracts (e.g., St John''s Wort), fluoroquinolones, phenothiazines, retinoids, halogenated salicylanilides (bacteriostatic soaps), sulfonamides, sulfonylureas, tetracyclines, thiazides, and certain organic staining dyes such as methylene blue, toluidine blue, rose bengal, and methyl orange.', 'DDInter', 1767317476, 'Caution is advised and pharmacologic response to methoxsalen therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided. Patients should be advised to avoid sun exposure, even through window glass or cloud cover, for at least 8 hours after methoxsalen ingestion and during the 24 hours following photochemotherapy or photopheresis treatment.', 'Synergism', 'Caution is advised and pharmacologic response to methoxsalen therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1401321, 'Dabrafenib', 'Methyl aminolevulinate (topical)', 'Moderate', 'Concomitant use of aminolevulinate topical preparations with other known photosensitizing agents may enhance the phototoxic reaction to photodynamic therapy. Medicinal products with known phototoxic or photoallergic potential include fluoroquinolones, phenothiazines, retinoids, sulfonamides, sulfonylureas, tetracyclines, thiazide diuretics, griseofulvin, and hypericin extracts (e.g., St John''s Wort).', 'DDInter', 1767317476, 'Caution is advised and pharmacologic response to photodynamic therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided. Patients should be advised to avoid exposure of treated areas to sunlight or bright indoor lights (e.g., examination lamps, operating room lamps, tanning beds, lights at close proximity) during the period between application of aminolevulinic acid or methyl aminolevulinate and photoactivation, and for 48 hours post-illumination. Concomitant use with other topical medicinal products should be avoided.', 'Synergism', 'Caution is advised and pharmacologic response to photodynamic therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
