INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1401362, 'Dabrafenib', 'Pexidartinib', 'Moderate', 'Coadministration of pexidartinib with moderate CYP450 3A4 inducers may decrease the plasma concentrations and therapeutic effects of pexidartinib. The proposed mechanism is induction of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of pexidartinib.', 'DDInter', 1767317476, 'Caution is advised if pexidartinib is used with moderate CYP450 3A4 inducers. It is unknown if pexidartinib will require a dosage adjustment if coadministered with a moderate CYP450 3A4 inducer. Clinical and laboratory monitoring of pexidartinib should be considered whenever a moderate CYP450 3A4 inducer is added to or withdrawn from therapy. Patients should be monitored for diminished therapeutic effects.', 'Metabolism', 'Caution is advised if pexidartinib is used with moderate CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
