INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (129129, 'Doxorubicin (liposomal)', 'Letermovir', 'Moderate', 'Coadministration with letermovir may increase the plasma concentrations of drugs that are substrates of CYP450 2C8, CYP450 3A4, and/or organic anion transporting polypeptide protein (OATP) 1B1 and 1B3. Letermovir has been shown to be a reversible inhibitor of CYP450 2C8 in vitro, although its effect on CYP450 2C8 substrates has not been evaluated clinically. Letermovir is also a time-dependent inhibitor and inducer of CYP450 3A4 in vitro.', NULL, 'Caution is advised when letermovir is used concurrently with drugs that are substrates of CYP450 2C8, CYP450 3A4, and/or OATP 1B1 and 1B3, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when letermovir is used concurrently with drugs that are substrates of CYP450 2C8, CYP450 3A4, and/or OATP 1B1 and 1B3, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever letermovir is added to or withdrawn from therapy. Moreover, clinicians should be aware that the magnitude of CYP450 3A- and OATP1B1/3-mediated drug interactions with coadministered drugs may be different when letermovir is used with cyclosporine. The combined effect of the two drugs on CYP450 3A4 may be similar to that of a strong CYP450 3A4 inhibitor, hence clinicians should refer to the prescribing information for dosing recommendations of the CYP450 3A4 substrate with a strong CYP450 3A4 inhibitor. Similarly, letermovir and cyclosporine may demonstrate some additive effects on OATP1B1 inhibition, although cyclosporine by itself is already a strong OATP1B1/3 inhibitor.', 'Metabolism', 'Idarubicin, Valrubicin, Plicamycin, Mitoxantrone, Bleomycin, Mitomycin, Daunorubicin, Dactinomycin, Epirubicin', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/129283/', '[1] "Product Information. Prevymis (letermovir)." Merck & Company Inc, Whitehouse Station, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (129130, 'Entrectinib', 'Letermovir', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of entrectinib, which is primarily metabolized by the isoenzyme. Increased exposure to entrectinib may increase the risk and/or severity of adverse effects such as cognitive impairment, mood disorders, dizziness, sleep disturbances, liver enzyme elevations, hyperuricemia, congestive heart failure, edema, myocarditis, QT prolongation, vision problems, anemia, and neutropenia.', NULL, 'Concomitant use of entrectinib with moderate CYP450 3A4 inhibitors should be avoided when possible.', NULL, 'Concomitant use of entrectinib with moderate CYP450 3A4 inhibitors should be avoided when possible. If coadministration is necessary, the dosage of entrectinib should be reduced to 100 mg once daily for adults and pediatrics patients (12 years and older) with a body surface area (BSA) greater than 1.5 m2. Following discontinuation, and after an appropriate washout period of the CYP450 3A4 inhibitor (i.e., 3 to 5 half-lives), the entrectinib dosage taken prior to initiating the CYP450 3A4 inhibitor may be resumed. For pediatric patients with a body surface area of 1.5 m2 or less, concomitant use of entrectinib with moderate CYP450 3A4 inhibitors should be avoided.', 'Metabolism', 'Sunitinib, Futibatinib, Regorafenib, Tucatinib, Vemurafenib, Lenvatinib, Erdafitinib, Trametinib, Vandetanib, Nintedanib, Ibrutinib, More', 'Lamivudine, Tenofovir alafenamide, Cabotegravir, Zanamivir, Valaciclovir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, Stavudine, Elbasvir, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/129284/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Rozlytrek (entrectinib)." Genentech, South San Francisco, CA.[3] "Product Information. Rozlytrek (entrectinib)." Genentech, South San Francisco, CA.[4] "Product Information. Rozlytrek (entrectinib)." Genentech, South San Francisco, CA.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (129131, 'Enzalutamide', 'Letermovir', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 and/or 3A4 may increase the plasma concentrations of enzalutamide, which is primarily metabolized by CYP450 2C8 to its pharmacologically active metabolite, N-desmethyl enzalutamide, and to a lesser extent by CYP450 3A4.', NULL, 'Pharmacologic response to enzalutamide should be monitored more closely whenever a CYP450 2C8 and/or 3A4 inhibitor is added to or withdrawn from therapy, and the dosage adjusted as necessary.', NULL, 'Pharmacologic response to enzalutamide should be monitored more closely whenever a CYP450 2C8 and/or 3A4 inhibitor is added to or withdrawn from therapy, and the dosage adjusted as necessary. Patients should be advised to contact their physician if they experience increased side effects such as seizure, asthenia, fatigue, diarrhea, arthralgia, musculoskeletal pain, paresthesia, hot flushes, peripheral edema, headache, dizziness, insomnia, hematuria, anxiety, and hypertension.', 'Metabolism', 'Anastrozole, Fulvestrant, Abarelix, Flutamide, Bicalutamide, Degarelix, Nilutamide, Aminoglutethimide, Abiraterone, Exemestane', 'Lamivudine, Cabotegravir, Zanamivir, Valaciclovir, Oseltamivir, Acyclovir, Brincidofovir, Stavudine, Elbasvir, Famciclovir, Voxilaprevir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/129285/', '[1] "Product Information. Xtandi (enzalutamide)." Astellas Pharma US, Inc, Deerfield, IL.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (129132, 'Eplerenone', 'Letermovir', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of eplerenone, which is primarily metabolized by the isoenzyme.', NULL, 'When used with moderate CYP450 3A4 inhibitors, the initial dosage of eplerenone should be reduced to 25 mg once daily for the treatment of hypertension.', NULL, 'When used with moderate CYP450 3A4 inhibitors, the initial dosage of eplerenone should be reduced to 25 mg once daily for the treatment of hypertension. Patients already on eplerenone who start treatment with a moderate CYP450 3A4 inhibitor should have serum potassium and creatinine checked in 3 to 7 days.', 'Metabolism', 'Amiloride, Triamterene, Spironolactone', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/129286/', '[1] "Product Information. Inspra (eplerenone)." Searle, Chicago, IL.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (129133, 'Idelalisib', 'Letermovir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may increase the plasma concentrations of idelalisib, which is a substrate of both the isoenzyme and efflux transporter.', NULL, 'Caution is advised if idelalisib is prescribed in combination with CYP450 3A4 and/or P-gp inhibitors.', NULL, 'Caution is advised if idelalisib is prescribed in combination with CYP450 3A4 and/or P-gp inhibitors. Pharmacologic response to idelalisib should be monitored more closely whenever a CYP450 3A4/P-gp inhibitor is added to or withdrawn from therapy, and the idelalisib dosing adjusted or interrupted as necessary in accordance with the product labeling. Patients should be closely monitored for idelalisib toxicity such as hepatotoxicity, diarrhea, colitis, intestinal perforation, pneumonitis, neutropenia, and thrombocytopenia.', 'Metabolism', 'Sunitinib, Futibatinib, Regorafenib, Tucatinib, Vemurafenib, Lenvatinib, Erdafitinib, Trametinib, Vandetanib, Nintedanib, Ibrutinib, More', 'Cabotegravir, Simeprevir, Zanamivir, Valaciclovir, Dolutegravir, Oseltamivir, Acyclovir, Famciclovir, Ribavirin, Tecovirimat, Maribavir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/129287/', '[1] "Product Information. Zydelig (idelalisib)." Gilead Sciences, Foster City, CA.[2] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (129134, 'Ifosfamide', 'Letermovir', 'Moderate', 'Coadministration with letermovir may increase the plasma concentrations of drugs that are substrates of CYP450 2C8, CYP450 3A4, and/or organic anion transporting polypeptide protein (OATP) 1B1 and 1B3. Letermovir has been shown to be a reversible inhibitor of CYP450 2C8 in vitro, although its effect on CYP450 2C8 substrates has not been evaluated clinically. Letermovir is also a time-dependent inhibitor and inducer of CYP450 3A4 in vitro.', NULL, 'Caution is advised when letermovir is used concurrently with drugs that are substrates of CYP450 2C8, CYP450 3A4, and/or OATP 1B1 and 1B3, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when letermovir is used concurrently with drugs that are substrates of CYP450 2C8, CYP450 3A4, and/or OATP 1B1 and 1B3, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever letermovir is added to or withdrawn from therapy. Moreover, clinicians should be aware that the magnitude of CYP450 3A- and OATP1B1/3-mediated drug interactions with coadministered drugs may be different when letermovir is used with cyclosporine. The combined effect of the two drugs on CYP450 3A4 may be similar to that of a strong CYP450 3A4 inhibitor, hence clinicians should refer to the prescribing information for dosing recommendations of the CYP450 3A4 substrate with a strong CYP450 3A4 inhibitor. Similarly, letermovir and cyclosporine may demonstrate some additive effects on OATP1B1 inhibition, although cyclosporine by itself is already a strong OATP1B1/3 inhibitor.', 'Metabolism', 'Temozolomide, Thiotepa, Carmustine, Busulfan, Streptozocin, Chlorambucil, Lomustine, Dacarbazine, Mechlorethamine, Bendamustine, Melphalan, More', 'Lamivudine, Paritaprevir, Glecaprevir, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Telaprevir, Velpatasvir, Rilpivirine, Etravirine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/129288/', '[1] "Product Information. Prevymis (letermovir)." Merck & Company Inc, Whitehouse Station, NJ.', 1767369485);
