INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (129149, 'Lurasidone', 'Letermovir', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of lurasidone and its active metabolite, both of which are primarily metabolized by the isoenzyme.', NULL, 'Lurasidone dosage should start at 20 mg/day and not exceed 80 mg/day when coadministered with moderate CYP450 3A4 inhibitors.', NULL, 'Lurasidone dosage should start at 20 mg/day and not exceed 80 mg/day when coadministered with moderate CYP450 3A4 inhibitors. If a moderate CYP450 3A4 inhibitor is added to existing lurasidone therapy, the lurasidone dosage should be reduced by half. Patients should be monitored for potentially increased adverse effects such as dizziness, drowsiness, dry mouth, increased appetite, weight gain, extrapyramidal symptoms, tardive dyskinesia, hyperglycaemia, dyslipidemia, hyperprolactinemia (galactorrhea, amenorrhea, gynecomastia), orthostatic hypotension, cognitive and motor impairment, seizures, dysphagia, and heat-related illnesses due to disruption of body temperature regulation.', 'Metabolism', 'Fluphenazine, Thiothixene, Risperidone, Amisulpride, Prochlorperazine, Promazine, Trifluoperazine, Paliperidone, Chlorpromazine, Mesoridazine, Methotrimeprazine, More', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/129303/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Latuda (lurasidone)." Sunovion Pharmaceuticals Inc, Marlborough, MA.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] "Product Information. Latuda (lurasidone)." Sunovion Pharmaceuticals Inc, Marlborough, MA.[7] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (129150, 'Lurbinectedin', 'Letermovir', 'Major', 'Coadministration with a moderate CYP450 3A4 inhibitor is predicted to increase lurbinectedin systemic exposure, which may increase the incidence and severity of adverse reactions to lurbinectedin, such as myelosuppression and hepatotoxicity. Concomitant use of lurbinectedin and moderate CYP450 3A4 inhibitors should generally be avoided. If the coadministration of lurbinectedin with a moderate CYP450 3A4 inhibitor cannot be avoided, a dose reduction for lurbinectedin may be considered.', NULL, 'Close clinical and laboratory monitoring is advised whenever a moderate inhibitor is added to or withdrawn from therapy.', NULL, 'Close clinical and laboratory monitoring is advised whenever a moderate inhibitor is added to or withdrawn from therapy. Patients should be advised to contact their physician if they experience symptoms suggestive of myelosuppression or hepatotoxicity, such as fever or any other signs of infection, unusual bruising or bleeding; nausea or vomiting, or pain on the right side of the abdomen.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Velpatasvir, More', 'Pertuzumab, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/129304/', '[1] "Product Information. Zepzelca (lurbinectedin)." Jazz Pharmaceuticals, Palo Alto, CA.[2] "Product Information. Zepzelca (lurbinectedin)." Jazz Pharmaceuticals, Pal