INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (129408, 'Hydroflumethiazide', 'Levosalbutamol', 'Moderate', 'Coadministration with beta-2 adrenergic agonists may potentiate the hypokalemic effects of potassium-wasting diuretics. Beta-2 agonists can cause clinically significant but usually transient decreases in serum potassium concentrations. Since QT prolongation is a possible side effect of beta-2 agonists, exacerbation of hypokalemia may increase the risk of torsade de pointes and other serious arrhythmias. The interaction may be more likely with systemic or nebulized formulations of beta-2 agonists, high dosages of inhaled beta-2 agonists, or concomitant theophylline or corticosteroid therapy.', NULL, 'Caution is advised when beta-2 agonists are used with potassium-wasting diuretics.', NULL, 'Caution is advised when beta-2 agonists are used with potassium-wasting diuretics. Serum potassium level and cardiovascular status should be monitored, especially if the beta-2 agonist is administered systemically or by nebulizer. Patients should be advised to notify their physician if they experience potential signs and symptoms of hypokalemia such as fatigue, weakness, myalgia, muscle cramps, numbness, tingling, abdominal pain, constipation, palpitation, and irregular heartbeat.', 'Synergism', 'Tamsulosin, Tinidazole, Ascorbic acid, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Amphotericin B, Clindamycin, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/129562/', '[1] "Product Information. Proventil (albuterol)." Schering Laboratories, Kenilworth, NJ.[2] "Product Information. Combivent (albuterol-ipratropium)." Boehringer-Ingelheim, Ridgefield, CT.[3] Lipworth BJ, McDevitt DG, Struthers AD "Prior treatment with diuretic augments the hypokalemic and electrocardiographic effects of inhaled albuterol." Am J Med 86 (1989): 653-7[4] "Product Information. Striverdi Respimat (olodaterol)." Boehringer Ingelheim, Ridgefield, CT.[5] "Product Information. Brovana (arformoterol)." Sepracor Inc, Marlborough, MA.[6] "Product Information. Breo Ellipta (fluticasone-vilanterol)." GlaxoSmithKline, Research Triangle Park, NC.[7] "Product Information. Brethaire (terbutaline)." Novartis Pharmaceuticals, East Hanover, NJ.[8] "Product Information. Arcapta Neohaler (indacaterol)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (129409, 'Hydroxyamphetamine (ophthalmic)', 'Levosalbutamol', 'Moderate', 'Coadministration of beta-2 adrenergic agonists with other adrenergic agents may potentiate the risk of cardiovascular side effects. Beta-2 adrenergic agonists can produce clinically significant cardiovascular effects including increases in pulse rate and systolic or diastolic blood pressure as well as ECG changes such as flattening of the T wave, prolongation of the QTc interval, and ST segment depression. The risk is lower when beta-2 adrenergic agonists are inhaled at normally recommended dosages.', NULL, 'Caution is advised if beta-2 adrenergic agonists are used concomitantly with other adrenergic agents, particularly in patients with cardiovascular disorders such as coronary insufficiency, cardiac arrhythmias, hypertrophic obstructive cardiomyopathy, or hypertension.', NULL, 'Caution is advised if beta-2 adrenergic agonists are used concomitantly with other adrenergic agents, particularly in patients with cardiovascular disorders such as coronary insufficiency, cardiac arrhythmias, hypertrophic obstructive cardiomyopathy, or hypertension. Blood pressure and heart rate should be closely monitored.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/129563/', '[1] Lowe MD, Rowland E, Brown MJ, Grace AA "Beta(2) adrenergic receptors mediate important electrophysiological effects in human ventricular myocardium." Heart 86 (2001): 45-51[2] "Product Information. Brovana (arformoterol)." Sepracor Inc, Marlborough, MA.[3] "Product Information. Breo Ellipta (fluticasone-vilanterol)." GlaxoSmithKline, Research Triangle Park, NC.[4] "Product Information. Maxair (pirbuterol)." 3M Pharmaceuticals, St. Paul, MN.[5] "Product Information. Arcapta Neohaler (indacaterol)." Novartis Pharmaceuticals, East Hanover, NJ.[6] "Product Information. Proventil (albuterol)." Schering Laboratories, Kenilworth, NJ.[7] Wong CS, Pavord ID, Williams J, Britton JR, Tattersfield AE "Bronchodilator, cardiovascular, and hypokalaemic effects of fenoterol, salbutamol, and terbutaline in asthma." Lancet 336 (1990): 1396-9[8] "Product Information. Foradil (formoterol)" Novartis Pharmaceuticals, East Hanover, NJ.[9] "Product Information. Striverdi Respimat (olodaterol)." Boehringer Ingelheim, Ridgefield, CT.[10] "Product Information. Severent (salmeterol)." Glaxo Wellcome, Research Triangle Park, NC.[11] "Product Information. Xopenex (levalbuterol)." Sepracor, Marlborough, MA.', 1767369485);
