    ) VALUES (1401676, 'Dacomitinib', 'Thioridazine', 'Major', 'Coadministration with inhibitors of CYP450 2D6 may increase the plasma concentrations of thioridazine and its two active metabolites, mesoridazine and sulforidazine, all of which are substrates of the isoenzyme.', NULL, 'The use of thioridazine with drugs that inhibit CYP450 2D6 is considered contraindicated.', NULL, 'The use of thioridazine with drugs that inhibit CYP450 2D6 is considered contraindicated. Depending on the elimination half-life of these drugs, a considerable waiting period may be appropriate following their discontinuation before thioridazine is initiated. For example, the manufacturer of fluoxetine recommends that thioridazine not be administered within 5 weeks after discontinuing fluoxetine because of the drug''s long half-life.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
