        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1401688, 'Dactinomycin', 'Chloramphenicol (ophthalmic)', 'Moderate', 'Coadministration of chloramphenicol with other agents that can cause bone marrow depression or aplastic anemia may increase the risk and/or severity of chloramphenicol hematologic toxicity. Serious and fatal blood dyscrasias (aplastic anemia, hypoplastic anemia, thrombocytopenia and granulocytopenia) have been reported after the administration of chloramphenicol.', NULL, 'Concurrent use of chloramphenicol with other bone marrow depressing agents should be avoided.', NULL, 'Concurrent use of chloramphenicol with other bone marrow depressing agents should be avoided.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
