INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (130431, 'Methoxamine', 'Levomilnacipran', 'Moderate', 'Additive or synergistic effects on blood pressure and heart rate may occur when serotonin-norepinephrine reuptake inhibitors (SNRIs) are combined with sympathomimetic agents such as nasal decongestants, adrenergic bronchodilators, ophthalmic vasoconstrictors, and systemic vasopressors. The use of SNRIs alone has been associated with sustained increases in blood pressure and heart rate, and cases of elevated blood pressure requiring immediate treatment have been reported in postmarketing experience.', NULL, 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate.', NULL, 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate. Blood pressure and pulse should be measured prior to initiating SNRI therapy and monitored at regular intervals thereafter. Dose reduction or discontinuation of the SNRI should be considered in patients who experience a sustained increase in blood pressure or pulse rate.', 'Synergism', 'Droxidopa, Amrinone, Angiotensin II, Milrinone, Arbutamine, Fenoldopam', 'Citalopram, Sertraline, Paroxetine, Maprotiline, Fluoxetine, St. John''s Wort, Nefazodone, Oxitriptan, Mirtazapine, Viloxazine, Vortioxetine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/130587/', '[1] "Product Information. Fetzima (levomilnacipran)." Forest Pharmaceuticals, St. Louis, MO.[2] "Product Information. Cymbalta (duloxetine)." Lilly, Eli and Company, Indianapolis, IN.[3] "Product Information. Nucynta (tapentadol)." PriCara Pharmaceuticals, Raritan, NJ.[4] "Product Information. Effexor (venlafaxine)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[5] "Product Information. Pristiq (desvenlafaxine)." Wyeth Laboratories, Philadelphia, PA.[6] "Product Information. Savella (milnacipran)." Forest Pharmaceuticals, St. Louis, MO.[7] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[8] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[9] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[10] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[11] "Grapefruit juice interactions with drugs." Med Lett Drugs Ther 37 (1995): 73-4[12] Maish WA, Hampton EM, Whitsett TL, Shepard JD, Lovallo WR "Influence of grapefruit juice on caffeine pharmacokinetics and pharmacodynamics." Pharmacotherapy 16 (1996): 1046-52', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (130432, 'Methsuximide', 'Levomilnacipran', 'Moderate', 'The efficacy of anticonvulsants may be diminished during coadministration with selective serotonin reuptake inhibitors (SSRIs) or serotonin-norepinephrine reuptake inhibitor (SNRIs). Antidepressants including SSRIs and SNRIs can reduce seizure threshold.Coadministration of SSRIs or SNRIs with some anticonvulsants, particularly carbamazepine, eslicarbazepine, oxcarbazepine and valproic acid, may increase the risk of hyponatremia. The risk appears to be dose-related, and elderly patients and patients who are volume depleted (e.g., diuretic use) may be at greater risk.', NULL, 'SSRIs and SNRIs should be avoided in patients with unstable epilepsy, and used cautiously in patients with epilepsy controlled with anticonvulsant medications.', NULL, 'SSRIs and SNRIs should be avoided in patients with unstable epilepsy, and used cautiously in patients with epilepsy controlled with anticonvulsant medications. Treatment with SSRIs and SNRIs should be discontinued if seizures develop or seizure frequency increases. Patients receiving SSRIs or SNRIs with anticonvulsants, particularly carbamazepine, eslicarbazepine, oxcarbazepine and/or valproic acid, should also have serum sodium levels measured regularly and monitored for development of hyponatremia, particularly when higher dosages of these medications are used. Signs and symptoms of hyponatremia include nausea, vomiting, headache, difficulty concentrating, memory impairment, confusion, malaise, lethargy, muscle weakness or spasms, and unsteadiness. In more severe and/or acute cases, hallucination, syncope, seizure, coma, respiratory arrest, and death may occur.', 'Antagonism', 'Clonazepam, Cannabidiol, Cenobamate, Carbamazepine, Fosphenytoin, Phenytoin, Primidone, Phenobarbital, Stiripentol', 'Oxitriptan, Bupropion, Viloxazine, Tryptophan', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/130588/', '[1] "Product Information. Savella (milnacipran)." Forest Pharmaceuticals, St. Louis, MO.[2] Belcastro V, Costa C, Striano P "Levetiracetam-associated hyponatremia." Seizure 17 (2008): 389-90[3] Gandhi S, McArthur E, Mamdani MM, et.al "Antiepileptic drugs and hyponatremia in older adults: Two population-based cohort studies." Epilepsia 57 (2016): 2067-79[4] "Product Information. Aptiom (eslicarbazepine)." Sunovion Pharmaceuticals Inc, Marlborough, MA.[5] "Product Information. Pristiq (desvenlafaxine)." Wyeth Laboratories, Philadelphia, PA.[6] "Product Information. Tegretol (carbamazepine)." Novartis Pharmaceuticals, East Hanover, NJ.[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[8] "Product Information. Paxil (paroxetine)." GlaxoSmithKline, Research Triangle Park, NC.[9] Falhammar H, Lindh JD, Calissendorff J, et.al "Differences in associations of antiepileptic drugs and hospitalization due to hyponatremia: A population-based case-control study." Seizure 59 (2018): 28-33[10] "Product Information. Trileptal (oxcarbazepine)" Novartis Pharmaceuticals, East Hanover, NJ.[11] Bavbek N, Alkan R, Uz E, Kaftan O, Akcay A "Hyponatremia associated with sodium valproate in a 22-year-old male." Nephrol Dial Transplant 23 (2008): epub[12] "Product Information. Prozac (fluoxetine)." Dista Products Company, Indianapolis, IN.[13] "Product Information. Effexor (venlafaxine)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[14] "Product Information. Fetzima (levomilnacipran)." Forest Pharmaceuticals, St. Louis, MO.[15] "Product Information. Lexapro (escitalopram)." Forest Pharmaceuticals, St. Louis, MO.[16] "Product Information. Cymbalta (duloxetine)." Lilly, Eli and Company, Indianapol