INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1401801, 'Isavuconazonium', 'Dalfampridine', 'Moderate', 'The concomitant use of dalfampridine with drugs that are inhibitors of organic cation transporter 2 (OCT2) may theoretically reduce its renal clearance. Dalfampridine is primarily eliminated through renal excretion, approximately 60% of which is via active secretion by OCT2. Elevated levels of dalfampridine may increase the risk of CNS excitation and seizures.', 'DDInter', 1767317476, 'Caution is advised when dalfampridine is used with OCT2 inhibitors. The benefit of concomitant use should be weighed against the potential risk of seizures.', 'Excretion', 'Caution is advised when dalfampridine is used with OCT2 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
