INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1401901, 'Dalteparin', 'Regorafenib', 'Major', 'Coadministration of regorafenib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Treatment with regorafenib is associated with a high frequency of hemorrhage and thrombocytopenia. Coadministration of regorafenib with strong UGT1A9 inhibitors such as mefenamic acid or diflunisal may theoretically increase systemic exposure to regorafenib.', 'DDInter', 1767317476, 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with regorafenib. Close clinical and laboratory observation for bleeding complications is recommended during therapy. The INR should be monitored more frequently during coadministration of warfarin.', 'Synergism', 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with regorafenib.', NULL, NULL, 'Major', 'pharmacodynamic');
