INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1401973, 'Escitalopram', 'Danazol', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 is not expected to significantly affect the pharmacokinetics of citalopram or escitalopram, both of which are partially metabolized by the isoenzyme. Administration of racemic citalopram (40 mg) in combination with the potent CYP450 3A4 inhibitor ketoconazole (200 mg) decreased the ketoconazole peak plasma concentration (Cmax) by 21% and systemic exposure (AUC) by 10%, but did not significantly affect the pharmacokinetics of citalopram.', 'DDInter', 1767317476, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1401974, 'Etravirine', 'Danazol', 'Moderate', 'Coadministration with inhibitors of CYP450 2C19, 2C9, and/or 3A4 may increase the plasma concentrations of etravirine, which is a substrate of these isoenzymes.', 'DDInter', 1767317476, 'Because safety data regarding increased etravirine exposures are limited, caution is advised if etravirine is prescribed in combination with CYP450 2C19, 2C9, and/or 3A4 inhibitors.', 'Metabolism', 'Because safety data regarding increased etravirine exposures are limited, caution is advised if etravirine is prescribed in combination with CYP450 2C19, 2C9, and/or 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1401975, 'Everolimus', 'Danazol', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein may increase the blood concentrations of everolimus following oral administration. Everolimus is a substrate of both the CYP450 3A4 isoenzyme and P-glycoprotein drug efflux transporter, thus their inhibition in the intestine can enhance the absorption of everolimus.', 'DDInter', 1767317476, 'The possibility of prolonged and/or increased pharmacologic effects of everolimus therapy should be considered during coadministration with CYP450 3A4 and/or P-glycoprotein inhibitors, including adverse effects such as pneumonitis, stomatitis, infection, dyspnea, diarrhea, anemia, leucopenia, thrombocytopenia, hyperglycemia, and hyperlipidemia. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.', 'Absorption', 'The possibility of prolonged and/or increased pharmacologic effects of everolimus therapy should be considered during coadministration with CYP450 3A4 and/or P-glycoprotein inhibitors, including adverse effects such as pneumonitis, stomatitis, infection, dyspnea, diarrhea, anemia, leucopenia, thrombocytopenia, hyperglycemia, and hyperlipidemia.', NULL, NULL, 'Moderate', 'pharmacodynamic');
