INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1402006, 'Ivabradine', 'Danazol', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ivabradine, which is primarily metabolized by the isoenzyme. Elevated plasma levels of ivabradine may increase the risk of excessive bradycardia and conduction disturbances.', 'DDInter', 1767317476, 'Caution is advised when ivabradine is prescribed with CYP450 3A4 inhibitors. Patients should be monitored for adverse effects such as bradycardia and atrial fibrillation, and the dosage of ivabradine adjusted as necessary in accordance with the product labeling', 'Metabolism', 'Caution is advised when ivabradine is prescribed with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1402007, 'Ivacaftor', 'Danazol', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ivacaftor, which is primarily metabolized by the isoenzyme. The interaction occurs to a greater extent when ivacaftor is administered as monotherapy than when administered in combination with lumacaftor, a potent CYP450 3A4 inducer.', 'DDInter', 1767317476, 'Caution is advised if ivacaftor is used with CYP450 3A4 inhibitors. A dosage adjustment for ivacaftor may be required if undue adverse effects occur.', 'Metabolism', 'Caution is advised if ivacaftor is used with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1402008, 'Danazol', 'Ixabepilone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ixabepilone, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Caution is advised when ixabepilone is prescribed with CYP450 3A4 inhibitors. Pharmacologic response to ixabepilone should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the ixabepilone dosage adjusted as necessary. Frequent monitoring of peripheral blood counts between treatment cycles is recommended, and patients should be advised to contact their physician if they experience signs and symptoms of ixabepilone toxicities such as infection or burning, tingling, or numbness in the hands and feet.', 'Metabolism', 'Caution is advised when ixabepilone is prescribed with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1402009, 'Danazol', 'Lapatinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of lapatinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Caution is advised when lapatinib is prescribed with CYP450 3A4 inhibitors. Pharmacologic response to lapatinib should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the lapatinib dosage adjusted as necessary. Patients should be advised to contact their physician if they experience increased adverse effects of lapatinib such as diarrhea, nausea, vomiting, dyspepsia, and fatigue.', 'Metabolism', 'Caution is advised when lapatinib is prescribed with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1402010, 'Danazol', 'Larotrectinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4, P-glycoprotein (P-gp), and/or breast cancer resistance protein (BCRP) may increase the plasma concentrations of larotrectinib.', 'DDInter', 1767317476, 'Caution is advised when larotrectinib is used with CYP450 3A4, P-gp, and/or BCRP inhibitors. Patients should be monitored for adverse effects such as neurotoxicity (delirium, dysarthria, dizziness, gait disturbance, paraesthesia, encephalopathy, memory impairment, tremor) and hepatotoxicity (elevations in liver transaminases), and the larotrectinib dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', 'Caution is advised when larotrectinib is used with CYP450 3A4, P-gp, and/or BCRP inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1402011, 'Danazol', 'Lefamulin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-gp may increase the plasma concentrations of lefamulin, which is a substrate of both the isoenzyme and efflux transporter.', 'DDInter', 1767317476, 'Caution is advised when lefamulin is used with CYP450 3A4 and/or P-gp inhibitors. Patients should be monitored for increased adverse effects and seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', 'Caution is advised when lefamulin is used with CYP450 3A4 and/or P-gp inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1402012, 'Danazol', 'Leflunomide', 'Major', 'The recent, concomitant, or subsequent use (without the recommended leflunomide washout period or procedure) of other agents known to induce hepatotoxicity may potentiate the risk of liver injury associated with leflunomide. The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide.', 'DDInter', 1767317476, 'Caution is advised if leflunomide or teriflunomide must be used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice), and vice versa. Liver enzymes and bilirubin should be measured prior to initiation of leflunomide/teriflunomide therapy and at least monthly for the first six months of treatment and every 6 to 8 weeks thereafter. Patients with preexisting liver disease or elevated baseline liver enzymes (i.e., ALT greater than two times ULN) should not receive leflunomide or teriflunomide. Patients who develop elevated serum ALT greater than three times ULN while receiving these medications should discontinue treatment and be given washout procedures with cholestyramine or activated charcoal to accelerate elimination of leflunomide''s active metabolite from plasma, which otherwise may take up to two years. Follow-up monitoring should be conducted at least weekly until the ALT value is within normal range, and washout procedures repeated as necessary. All patients treated with leflunomide or teriflunomide should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'Caution is advised if leflunomide or teriflunomide must be used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1402013, 'Danazol', 'Lemborexant', 'Major', 'Coadministration with weak inhibitors of CYP450 3A4 may increase the plasma concentrations of lemborexant, which is primarily metabolized by the isoenzyme. Concomitant use of a weak CYP450 3A4 inhibitor is predicted to increase lemborexant exposure by less than 2-fold. Increased exposure to lemborexant may increase the risk of adverse reactions such as central nervous system (CNS) depression, sleep paralysis, hallucinations, complex sleep behaviors, worsening of depression or suicidal ideation, nightmares, palpitations, or headache.', 'DDInter', 1767317476, 'The lemborexant dose should be limited to 5 mg no more than once per night when coadministered with weak CYP450 3A4 inhibitors.', 'Metabolism', 'The lemborexant dose should be limited to 5 mg no more than once per night when coadministered with weak CYP450 3A4 inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1402014, 'Danazol', 'Levobupivacaine', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 and/or 3A4 may increase the plasma concentrations of levobupivacaine, which is primarily metabolized by these isoenzymes.', 'DDInter', 1767317476, 'Caution may be advisable if levobupivacaine is administered to patients receiving inhibitors of CYP450 1A2 (e.g., quinolones, deferasirox, mexiletine, propafenone, ticlopidine, zileuton) or CYP450 3A4 (e.g., azole antifungals, macrolide antibiotics, protease inhibitors, kinase inhibitors, aprepitant, conivaptan, cyclosporine, delavirdine, diltiazem, nefazodone, verapamil). Particular caution and dosage adjustment of levobupivacaine may be warranted during concomitant use of both a CYP450 1A2 inhibitor and a CYP450 3A4 inhibitor, or a drug that inhibits both isoenzymes (e.g., fluvoxamine). Patients should be monitored for early signs and symptoms of central nervous system toxicity such as dizziness, drowsiness, lightheadedness, anxiety, restlessness, incoherent speech, tremors, twitching, numbness and tingling of the mouth and lips, metallic taste, tinnitus, and blurred vision, as well as more serious reactions including convulsions, hypotension, arrhythmias, and cardiorespiratory depression.', 'Metabolism', 'Caution may be advisable if levobupivacaine is administered to patients receiving inhibitors of CYP450 1A2 (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1402015, 'Danazol', 'Levacetylmethadol', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations and duration of action of levomethadyl acetate, which is a substrate of the isoenzyme. High plasma levels of levomethadyl acetate may increase the risk of QT interval prolongation, which is thought to be associated with serious cardiac events such as ventricular arrhythmias (e.g., torsade de pointes, ventricular tachycardia), cardiac arrest, and sudden death. Cases of QT prolongation and torsade de pointes have been reported with levomethadyl acetate alone during postmarketing use.', 'DDInter', 1767317476, 'Concomitant use of levomethadyl acetate with CYP450 3A4 inhibitors is considered contraindicated.', 'Metabolism', 'Concomitant use of levomethadyl acetate with CYP450 3A4 inhibitors is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1402016, 'Danazol', 'Levothyroxine', 'Moderate', 'Androgens may induce reversible clinical hyperthyroidism in patients receiving thyroid hormone replacement therapy. The proposed mechanism is androgen-induced decrease in T4 binding globulin resulting in decreased serum T4, increased T3 uptake resin and free T4, and decreased TSH levels.', 'DDInter', 1767317476, 'Clinical and laboratory monitoring of thyroid function may be necessary, as may a 25% to 50% reduction in thyroid hormone dosage.', 'Synergism', 'Clinical and laboratory monitoring of thyroid function may be necessary, as may a 25% to 50% reduction in thyroid hormone dosage.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1402017, 'Danazol', 'Linagliptin', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 1767317476, 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Antagonism', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
