INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (130793, 'Dexbrompheniramine', 'Linezolid', 'Moderate', 'Coadministration of monoamine oxidase inhibitors (MAOIs) and antihistamines may result in additive central nervous system depressant effects. In addition, limited data suggest that MAOIs may potentiate and prolong the anticholinergic effects of antihistamines due to inhibition of catecholamine degradation, which may lead to overstimulation of the sympathetic nervous system.', NULL, 'Prescribing antihistamines in combination with MAOIs or other agents that possess MAOI activity (e.', NULL, 'Prescribing antihistamines in combination with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, procarbazine) should generally be avoided. If concomitant treatment is unavoidable, patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/130949/', '[1] Kahn DA "Possible toxic interaction between cyproheptadine and phenelzine." Am J Psychiatry 144 (1987): 1242-3[2] "Product Information. Matulane (procarbazine)." Roche Laboratories, Nutley, NJ.[3] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[4] Ferry T, Ponceau B, Simon M, et al. "Possibly linezolid-induced peripheral and central neurotoxicity: report of four cases." Infection 33 (2005): 151-4[5] Cerner