INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (131265, 'Relugolix', 'Lisdexamfetamine', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', NULL, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', NULL, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval. Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/131421/', '[1] "Product Information. Vantas (histrelin)." Endo Pharmaceuticals (formally Indevus Pharmaceuticals Inc), Lexington, MA.[2] "Product Information. Zoladex (goserelin)." Zeneca Pharmaceuticals, Wilmington, DE.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Trelstar (triptorelin)" Pharmacia and Upjohn, Kalamazoo, MI.[5] "Product Information. Firmagon (degarelix)." Ferring Pharmaceuticals Inc, Tarrytown, NY.[6] "Product Information. Lupron (leuprolide)." TAP Pharmaceuticals Inc, Deerfield, IL.[7] "Product Information. Eligard (leuprolide)." Sanofi Winthrop Pharmaceuticals, New York, NY.[8] "Product Information. Plenaxis (abarelix)." Praecis Pharmaceuticals Inc, Waltham, MA.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (131266, 'Remifentanil', 'Lisdexamfetamine', 'Moderate', 'Coadministration of amphetamines and amphetamine-like drugs with certain opioids or the synthetic opioid derivative, dextromethorphan, may increase the risk of serotonin syndrome. Dextromethorphan and some opioids, particularly those in the phenylpiperidine class, are weak serotonin reuptake inhibitors whose effects may be potentiated by amphetamines.', NULL, 'Caution is recommended when amphetamines and amphetamine-like drugs are used with dextromethorphan or opioids with serotonergic effects such as fentanyl, meperidine, methadone, propoxyphene, and tramadol.', NULL, 'Caution is recommended when amphetamines and amphetamine-like drugs are used with dextromethorphan or opioids with serotonergic effects such as fentanyl, meperidine, methadone, propoxyphene, and tramadol. Patients should be monitored for symptoms of the serotonin syndrome during treatment. Particular caution is advised when increasing the dosages of these agents. If serotonin syndrome develops or is suspected during the course of therapy, all serotonergic agents should be discontinued immediately and supportive care rendered as necessary. Moderately ill patients may also benefit from the administration of a serotonin antagonist (e.g., cyproheptadine, chlorpromazine). Severe cases should be managed under consultation with a toxicologist and may require sedation, neuromuscular paralysis, intubation, and mechanical ventilation in addition to the other measures.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/131422/', '[1] Mugele J, Nanagas KA, Tormoehlen LM "Serotonin Syndrome Associated With MDPV Use: A Case Report." Ann Emerg Med (2012):[2] Gillman PK "Monoamine oxidase inhibitors, opioid analgesics and serotonin toxicity." Br J Anaesth (2005):[3] "Product Information. Sanorex (mazindol)" Novartis Pharmaceuticals, East Hanover, NJ.[4] "Product Information. Desoxyn (methamphetamine)" Abbott Pharmaceutical, Abbott Park, IL.[5] "Product Information. Nuedexta (dextromethorphan-quinidine)." Avanir Pharmaceuticals, Inc, Aliso Viejo, CA.[6] "Product Information. Adipex-P (phentermine)." Teva Pharmaceuticals (formerly Gate Pharmaceuticals), Sellersville, PA.[7] "Product Information. Adderall (amphetamine-dextroamphetamine)" Shire Richwood Pharmaceutical Company, Florence, KY.[8] Davis JJ, Buck NS, Swenson JD, Johnson KB, Greis PE "Serotonin syndrome manifesting as patient movement during total intravenous anesthesia with propofol and remifentanil." J Clin Anesth 25 (2013): 52-4[9] "Product Information. Vyvanse (lisdexamfetamine)." Shire US Inc, Florence, KY.[10] Lee J, Franz L, Goforth HW "Serotonin syndrome in a chronic-pain patient receiving concurrent methadone, ciprofloxacin, and venlafaxine." Psychosomatics 50 (2009): 638-9[11] Hunter B, Kleinert MM, Osatnik J, Soria E "Serotonergic syndrome and abnormal ocular movements: worsening of rigidity by remifentanil?" Anesth Analg 102 (2006): 1589[12] Mendelson J, Jones RT, Upton R, Jacob P 3rd "Methamphetamine and ethanol interactions in humans." Clin Pharmacol Ther 57 (1995): 559-68[13] "Product Information. Didrex (benzphetamine)" Pharmacia and Upjohn, Kalamazoo, MI.[14] "Product Information. Suprenza (phentermine)." Akrimax Pharmaceuticals, Cranford, NJ.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (131267, 'Reserpine', 'Lisdexamfetamine', 'Moderate', 'Central nervous system (CNS) stimulants, particularly the amphetamines, may decrease the hypotensive effect of postganglionic adrenergic blocking agents such as guanadrel, guanethidine and rauwolfia alkaloids, which work by depleting catecholamine stores from adrenergic nerve endings. CNS stimulants can have peripheral sympathetic activity, thus they may elevate blood pressure on their own as well as antagonize some of the antiadrenergic effect produced by these hypotensive agents.', NULL, 'Alternatives to postganglionic adrenergic blocking agents should be considered in hypertensive patients treated with CNS stimulants.', NULL, 'Alternatives to postganglionic adrenergic blocking agents should be considered in hypertensive patients treated with CNS stimulants. If the combination is used, blood pressure and heart rate should be monitored.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/131423/', '[1] Gerkens JF, McCulloch MW, Wilson J "Mechanism of the antagonism between guanethidine and dexamphetamine." Br J Pharmacol 35 (1969): 563-72[2] Flegin OT, Morgan DH, Oates JA, Shand DG, Turner P "The mechanism of the reversal of the effect of guanethidine by amphetamines in cat and man." Br J Pharmacol 39 (1970): p253[3] Gulati OD, Dave BT, Gokhale SD, Shah KM "Antagonism of adrenergic neuron blockade in hypertensive subjects." Clin Pharmacol Ther 7 (1966): 510-4[4] Sherman GP, Walton CA "Adrenergic transmission and drug interaction." J Am Pharm Assoc 15 (1975): 86-90[5] Obianwu HO "Some studies on the mechanism by which d-amphetamine ant