INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (131333, 'Enoxaparin', 'Lisinopril', 'Moderate', 'The concomitant use of angiotensin converting enzyme (ACE) inhibitors or angiotensin receptor blockers (ARBs) and heparin or low molecular weight heparins may increase the risk of hyperkalemia. ACE inhibitors, ARBs, and some heparins individually have been associated with increased potassium levels or hyperkalemia.', NULL, 'Caution is advised if ACE inhibitors or ARBs are used with heparin or low molecular weight heparin.', NULL, 'Caution is advised if ACE inhibitors or ARBs are used with heparin or low molecular weight heparin. Serum potassium and renal function should be checked regularly.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/131489/', '[1] "Product Information. Micardis (telmisartan)." Boehringer-Ingelheim, Ridgefield, CT.[2] "Product Information. Diovan (valsartan)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Avapro (irbesartan)." Bristol-Myers Squibb, Princeton, NJ.[4] "Product Information. Atacand (candesartan)." Astra Pharmaceuticals, Wayne, PA.[5] "Product Information. Teveten (eprosartan)." SmithKline Beecham, Philadelphia, PA.[6] "Product Information. Benicar (olmesartan)." Sankyo Parke Davis, Parsippany, NJ.[7] Perazella MA "Drug-induced hyperkalemia: old culprits and new offenders." Am J Med 109 (2000): 307-14[8] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[9] "Product Information. Cozaar (losartan)." Merck & Co, Inc, West Point, PA.[10] "Product Information. Edarbi (azilsartan)." Takeda Pharmaceuticals America, Lincolnshire, IL.[11] Ray K, Dorman S, Watson R "Severe hyperkalaemia due to the concomitant use of salt substitutes and ACE inhibitors in hypertension: a potentially life threatening interaction." J Hum Hypertens 13 (1999): 717-20[12] "Product Information. Vasotec (enalapril)." Merck & Co, Inc, West Point, PA.[13] Good CB, McDermott L "Diet and serum potassium in patients on ACE inhibitors." JAMA 274 (1995): 538', 1767363024);
