INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1402893, 'Hyaluronidase', 'Astemizole', 'Minor', 'Larger amounts of hyaluronidase may be required to achieve equivalent dispersing effects if patients are concomitantly receiving large doses of salicylates, cortisone, ACTH, estrogens, or antihistamines. These drugs may render tissues partially resistant to the dispersing action of hyaluronidase.', 'DDInter', 1767317476, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
