INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (131713, 'Pimavanserin', 'Lithium carbonate', 'Moderate', 'Pimavanserin may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of pimavanserin with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of pimavanserin with other drugs that can prolong the QT interval should generally be avoided. Caution and clinical monitoring are recommended if concomitant use is required. Patients should have regular ECGs and be monitored for arrhythmias when QT interval is prolonged.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/131870/', '[1] "Product Information. Nuplazid (pimavanserin)." Accelis Pharma, East Windsor, NJ.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (131714, 'Pimozide', 'Lithium carbonate', 'Moderate', 'Coadministration of lithium with neuroleptic agents, particularly haloperidol, has been associated with rare cases of an encephalopathic syndrome characterized by weakness, lethargy, fever, tremors, confusion, extrapyramidal symptoms, leukocytosis, and elevated liver enzymes and blood urea nitrogen. This syndrome may be similar to, or the same as, neuroleptic malignant syndrome. Other, more common central nervous system effects may also be increased, such as dizziness, drowsiness, confusion, difficulty concentrating, and impairment in thinking, judgment, and motor coordination.', NULL, 'Close monitoring for central nervous system adverse effects is recommended when lithium is used with neuroleptic agents.', NULL, 'Close monitoring for central nervous system adverse effects is recommended when lithium is used with neuroleptic agents. Dosage adjustments or discontinuation of one or both drugs may be necessary if an interaction is suspected.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/131871/', '[1] Swanson CL, Price WA, Mcevoy JP "Effects of concomitant risperidone and lithium treatment." Am J Psychiatry 152 (1995): 1096[2] Goldman SA "FDA medwatch report: lithium and neuroleptics in combination: the spectrum of neurotoxicity." Psychopharmacol Bull 32 (1996): 299-309[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylo