INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (131733, 'Protriptyline', 'Lithium carbonate', 'Moderate', 'The concomitant use of lithium and tricyclic antidepressants (TCAs) may potentiate the risk of neurotoxicity and psychotic symptoms, even at therapeutic serum lithium levels. The mechanism is unknown but may be related to synergistic effects on neurotransmission. Mania, myoclonus, tremors, memory difficulties, confusion, serotonin syndrome, and a neuroleptic malignant-like syndrome have been reported in patients treated with TCAs several days after the addition of lithium, in most cases requiring discontinuation of either the TCA or lithium. Elderly patients may be particularly susceptible to this interaction.', NULL, 'Close monitoring for symptoms of neurotoxicity is recommended if TCAs are used with lithium.', NULL, 'Close monitoring for symptoms of neurotoxicity is recommended if TCAs are used with lithium. Dosage adjustment or discontinuation of one or both drugs may be necessary if an interaction is suspected.', 'Synergism', 'Viloxazine', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/131890/', '[1] Kojima H, Terao T, Yoshimura R "Serotonin syndrome during clomipramine and lithium treatment." Am J Psychiatry 150 (1993): 1897[2] Devanand DP, Sackeim HA, Brown RP "Myoclonus during combined tricyclic antidepressant and lithium treatment." J Clin Psychopharmacol 8 (1988): 446-7[3] Delisle JD "Rapid switch in a bipolar patient during lithium-tricyclic therapy." Am J Psychiatry 143 (1986): 1326-7[4] Austin LS, Arana GW, Melvin JA "Toxicity resulting from lithium augmentation of antidepressant treatment in elderly patients." J Clin Psychiatry 51 (19