INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (131929, 'Tolbutamide', 'Lixisenatide', 'Moderate', 'Coadministration of a glucagon-like peptide-1 (GLP-1) receptor agonist with an insulin secretagogue (e.g., sulfonylurea, meglitinide) may potentiate the risk of hypoglycemia. GLP-1 receptor agonists lower blood glucose by stimulating insulin secretion and lowering glucagon secretion.', NULL, 'A lower dosage of the insulin secretagogue may be required when used in combination with a GLP-1 receptor agonist.', NULL, 'A lower dosage of the insulin secretagogue may be required when used in combination with a GLP-1 receptor agonist. Blood glucose should be monitored closely, and patients should be counseled to recognize the symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia. If hypoglycemia occurs, patients should initiate appropriate remedial therapy immediately and contact their physician. Patients should also be advised to take precautions to avoid hypoglycemia while driving or operating hazardous machinery.', 'Synergism', 'Methacholine, Hexaminolevulinate, Secretin human, Methylene blue, Sorbitol, Galactose, Magnesium sulfate, Vitamin A, Caffeine, Metyrapone, Edrophonium, More', 'Glipizide, Glimepiride, Acetohexamide, Acarbose, Troglitazone, Chlorpropamide, Rosuvastatin, Tolazamide, Repaglinide, Glyburide, Tolbutamide, Pioglitazone', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/132086/', '[1] "Product Information. Trulicity (dulaglutide)." Eli Lilly and Company, Indianapolis, IN.[2] "Product Information. Adlyxin (lixisenatide)." sanofi-aventis, Bridgewat