INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (131941, 'Icosapent', 'Dextran (low molecular weight)', 'Moderate', 'Omega-3 fatty acids (e.g., fish oil) may potentiate the pharmacologic effects of anticoagulants and other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, dextran, and nonsteroidal anti-inflammatory drugs (NSAIDs). The exact mechanism of interaction is unknown. Omega-3 fatty acids may possess mild antiplatelet and hypocoagulant activities.', NULL, 'In general, patients should consult a healthcare provider before taking any herbal or nutritional supplements.', NULL, 'In general, patients should consult a healthcare provider before taking any herbal or nutritional supplements. Patients using omega-3 fatty acids in combination with anticoagulants or other drugs that affect hemostasis should be advised of the potential for increased risk of bleeding complications.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/132098/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Li XL, Steiner M "Fish oil: a potent inhibitor of platelet adhesiveness." Blood 76 (1990): 938-45[3] "Product Information. Vascepa (icosapent)." Amarin Pharmaceuticals Inc, Warren, NJ.[4] Vanschoonbeek K, Feijge MA, Paquay J, et al "Variable hypocoagulant effect of fish oil intake in humans: modulation of fibrinogen level and thrombin generation." Arterioscler Thromb Vasc Biol 24 (2004): 1734-40[5] "Product Information. Omacor (omega-3 polyunsaturated fatty acids)." Abbott Pharmaceutical, Abbott Park, IL.[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] Buckley MS, Goff