INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (131943, 'Dextran (low molecular weight)', 'Omacetaxine mepesuccinate', 'Major', 'Coadministration of omacetaxine and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Treatment with omacetaxine is associated with a high frequency of thrombocytopenia.', NULL, 'Concomitant use of other medications that interfere with platelet function or coagulation should be avoided if the patient''s platelet count is less than 50,000 per microliter.', NULL, 'Concomitant use of other medications that interfere with platelet function or coagulation should be avoided if the patient''s platelet count is less than 50,000 per microliter. Close clinical and laboratory observation for bleeding complications is recommended during therapy. The CBC and platelet count should be monitored according to the manufacturer''s recommendations.', 'Synergism', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/132100/', '[1] "Product Information. Synribo (omacetaxine)." Teva Pharmaceuticals USA, North Wales, PA.[2] "Product Information. Brukinsa (zanubrutinib)." BeiGene USA, Inc, San Mateo, CA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (131944, 'Dextran (low molecular weight)', 'Pentoxifylline', 'Moderate', 'The use of pentoxifylline has been associated with bleeding and/or prolongation in prothrombin time. Theoretically, the risk of bleeding may be increased by concomitant treatment with anticoagulants or other agents that affect hemostasis such as nonsteroidal anti-inflammatory drugs (NSAIDs), platelet inhibitors, thrombin inhibitors, thrombolytic agents, and dextran. There have been postmarketing reports of increased anticoagulant activity in patients receiving vitamin K antagonists in combination with pentoxifylline.', NULL, 'Caution is advised if pentoxifylline is administered with anticoagulants or other agents that affect hemostasis.', NULL, 'Caution is advised if pentoxifylline is administered with anticoagulants or other agents that affect hemostasis. Patients on vitamin K antagonists such as warfarin should have more frequent monitoring of INR following initiation or dosage change of pentoxifylline, while patients receiving other agents that affect hemostasis should have periodic examinations for bleeding including hematocrit and/or hemoglobin.', 'Synergism', 'Cyclandelate, Tolazoline, Phenoxybenzamine, Phentolamine, Isoxsuprine, Niacin, Theophylline, Omalizumab, Tezepelumab, Mercaptopurine, Aminophylline, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/132101/', '[1] "Product Information. Trental (pentoxifylline)." Hoechst Marion-Roussel Inc, Kansas City, MO.[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (131945, 'Dextran (low molecular weight)', 'Prasugrel', 'Major', 'Concomitant use of prasugrel with other agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, dextran, nonsteroidal anti-inflammatory agents (chronic), or agents that commonly cause thrombocytopenia may increase the risk of bleeding. Prasugrel may be used with aspirin, heparin, or glycoprotein IIb/IIIa inhibitors.', NULL, 'Caution is advised if prasugrel is used in combination with other drugs that affect hemostasis.', NULL, 'Caution is advised if prasugrel is used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hemorrhagic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Ticagrelor, Clopidogrel, Cilostazol, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Warfarin, Argatroban, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/132102/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Effient (prasugrel)." Lilly, Eli and Company, Indianapolis, IN.', 1767369485);
