INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1402929, 'Enalapril', 'Darbepoetin alfa', 'Minor', 'Angiotensin-converting enzyme (ACE) inhibitors may interfere with the effects of both endogenous and exogenous erythropoietin. Until further data are available, it may be appropriate to monitor patients for potentially diminished hematopoietic response to recombinant erythropoietin therapy following the addition of an ACE inhibitor. It is not known if other erythropoiesis-stimulating agents are similarly affected.', 'DDInter', 1767317476, '-', 'Antagonism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1402930, 'Darbepoetin alfa', 'Lenalidomide', 'Major', 'Concomitant treatment with agents that can cause thrombosis such as erythropoiesis-stimulating agents, estrogens, selective estrogen receptor modulators, or C1 esterase inhibitors may potentiate the risk of venous thromboembolic events associated with the use of lenalidomide. Deep vein thrombosis (DVT) and pulmonary embolism (PE) have been observed at significantly increased rates when lenalidomide was coadministered with dexamethasone for the treatment of multiple myeloma.', 'DDInter', 1767317476, 'Agents that are known to cause thrombosis should be used with caution in multiple myeloma patients receiving lenalidomide with dexamethasone. Patients should be advised to seek medical attention if they develop potential signs and symptoms of thromboembolism such as chest pain; shortness of breath; rapid pulse; pain, swelling, and/or discoloration in an arm or leg; and numbness or weakness on one side of the body. It is not known whether prophylactic anticoagulation or antiplatelet therapy may lessen the risk of venous thromboembolic events. The decision to take prophylactic measures should be done carefully after a thorough assessment of underlying risk factors. If a thromboembolic event occurs during therapy with lenalidomide, treatment must be discontinued and standard anticoagulation therapy initiated. Once anticoagulation is stabilized and complications of the thromboembolic event under control, lenalidomide may be restarted at the original dose if benefit is deemed to outweigh the risks. Anticoagulation therapy should be continued during the remaining course of lenalidomide treatment.', 'Synergism', 'Agents that are known to cause thrombosis should be used with caution in multiple myeloma patients receiving lenalidomide with dexamethasone.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1402931, 'Darbepoetin alfa', 'Lisinopril', 'Minor', 'Angiotensin-converting enzyme (ACE) inhibitors may interfere with the effects of both endogenous and exogenous erythropoietin. Until further data are available, it may be appropriate to monitor patients for potentially diminished hematopoietic response to recombinant erythropoietin therapy following the addition of an ACE inhibitor. It is not known if other erythropoiesis-stimulating agents are similarly affected.', 'DDInter', 1767317476, '-', 'Antagonism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
