INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403007, 'Haloperidol', 'Darifenacin', 'Moderate', 'Coadministration with darifenacin may increase the plasma concentrations of drugs that are metabolized by CYP450 2D6, including many psychotherapeutic drugs such as tricyclic antidepressants, phenothiazines, and other neuroleptics. The mechanism is decreased clearance due to inhibition of the CYP450 2D6 isoenzyme by darifenacin.', 'DDInter', 1767317476, 'Caution is advised if darifenacin must be used concomitantly with tricyclic antidepressants, phenothiazines, or other neuroleptics. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever darifenacin is added to or withdrawn from therapy. Clinicians should also be alerted to the potential for additive anticholinergic effects during concomitant administration. Patients should be advised to notify their physician promptly if they experience potential symptoms of anticholinergic intoxication such as abdominal pain, fever, heat intolerance, blurred vision, confusion, and/or hallucinations.', 'Metabolism', 'Caution is advised if darifenacin must be used concomitantly with tricyclic antidepressants, phenothiazines, or other neuroleptics.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403008, 'Hydroflumethiazide', 'Darifenacin', 'Minor', 'Anticholinergic agents may increase the absorption and oral bioavailability of thiazide diuretics. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents.', 'DDInter', 1767317476, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403009, 'Hydromorphone', 'Darifenacin', 'Moderate', 'Coadministration of opioids with anticholinergic agents may result in additive central nervous system (CNS), gastrointestinal, and genitourinary effects. The risk and/or severity of adverse effects such as sedation, dizziness, confusion, cognitive and psychomotor impairment, dry mouth, constipation, and urinary retention may increase. Severe constipation may lead to paralytic ileus in some cases.', 'DDInter', 1767317476, 'Caution and close monitoring of central nervous system, gastrointestinal, and genitourinary adverse effects are recommended when opioids are used with anticholinergic agents. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'Caution and close monitoring of central nervous system, gastrointestinal, and genitourinary adverse effects are recommended when opioids are used with anticholinergic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
