INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403177, 'Darifenacin', 'Troglitazone', 'Moderate', 'Theoretically, coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of darifenacin, which has been shown to be a substrate of the isoenzyme.', 'DDInter', 1767317476, 'Pharmacologic response to darifenacin should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the darifenacin dosage adjusted if necessary.', 'Metabolism', 'Pharmacologic response to darifenacin should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the darifenacin dosage adjusted if necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403178, 'Darifenacin', 'Troleandomycin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of darifenacin, which is partially metabolized by the isoenzyme. Darifenacin is also metabolized by CYP450 2D6, but 3A4 is the major pathway of metabolism in so-called poor metabolizers of 2D6.', 'DDInter', 1767317476, 'The dosage of darifenacin should not exceed 7.5 mg/day when used with potent CYP450 3A4 inhibitors. Some authorities recommend avoiding concomitant use of darifenacin during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'The dosage of darifenacin should not exceed 7.', NULL, NULL, 'Major', 'pharmacodynamic');
