INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (132359, 'Vinblastine', 'Lomustine', 'Minor', 'An in vitro test has shown lomustine to have inhibitory effects on the biotransformation of vinblastine. Both drugs are believed to be metabolized by the liver cytochrome P-450. The clinical significance of this interaction has not been established. If lomustine and vinblastine are used concomitantly, caution and close observation are advised.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/132516/', '[1] Zhou-Pan XR, Seree E, Zhou XJ, et al "Involvement of human liver cytochrome P450 3A in vinblastine metabolism: drug interactions." Cancer Res 53 (1993): 5121-6', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (132360, 'Vincristine', 'Lomustine', 'Minor', 'Concurrent administration of vincristine and lomustine may lead to an earlier onset and/or increased severity of side effects. The mechanism is the inhibition of the metabolism of both drugs by a hepatic cytochrome P-450 isoenzyme in the CYP 3A subfamily. The clinical significance of this interaction has not been established. If vincristine and lomustine are used concomitantly, caution and close observation are advised.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/132517/', '[1] "Product Information. Oncovin (vincristine)." Lilly, Eli and Company, Indianapolis, IN.[2] Zhou-Pan XR, Seree E, Zhou XJ, et al "Involvement of human liver cytochrome P450 3A in vinblastine metabolism: drug interactions." Cancer Res 53 (1993): 5121-6', 1767363024);
