INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403539, 'Darunavir', 'Quinidine', 'Moderate', 'Coadministration with darunavir may increase the plasma concentrations of amiodarone, bepridil, systemic lidocaine, and quinidine. The proposed mechanism is darunavir inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of these antiarrhythmic agents. The interaction has not been studied but could conceivably lead to serious and/or life-threatening reactions including cardiac arrhythmias and other toxicities.', 'DDInter', 1767317476, 'Caution is advised if darunavir must be used with amiodarone, bepridil, systemic lidocaine, or quinidine. Pharmacologic response and plasma antiarrhythmic drug levels should be monitored more closely whenever darunavir is added to or withdrawn from therapy, and the antiarrhythmic dosage adjusted as necessary.', 'Metabolism', 'Caution is advised if darunavir must be used with amiodarone, bepridil, systemic lidocaine, or quinidine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403540, 'Darunavir', 'Quinine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of quinine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Caution is advised if quinine is used in combination with potent and moderate CYP450 3A4 inhibitors. Patients should be monitored closely for adverse reactions associated with quinine such as hematologic toxicities and cardiac arrhythmias including torsade de pointes and atrial fibrillation. Patients should be advised to contact their physician if they experience increased side effects such as headache, flushing, sweating, nausea, vomiting, diarrhea, abdominal pain, tinnitus, dizziness, vertigo, hearing impairment, blurred vision, vision impairment, and irregular heart rhythm.', 'Metabolism', 'Caution is advised if quinine is used in combination with potent and moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
