INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403578, 'Darunavir', 'Sonidegib', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of sonidegib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Concomitant use of sonidegib with moderate CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends limiting use of the moderate CYP450 3A4 inhibitor to less than 14 days and monitoring for adverse reactions, particularly musculoskeletal toxicity.', 'Metabolism', 'Concomitant use of sonidegib with moderate CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403579, 'Darunavir', 'St. John''s Wort', 'Major', 'Coadministration with St. John''s wort may significantly reduce the plasma concentrations of HIV protease inhibitors and result in a potential loss of virologic response. The mechanism is induction of CYP450 3A4 metabolism by constituents of St. John''s wort.', 'DDInter', 1767317476, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of HIV protease inhibitors with St. John''s wort is considered contraindicated. The enzyme induction may last for up to 2 weeks after the last dose of St. John''s wort. Patients should be advised to consult with their caregivers before using any herbal or alternative medicines.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of HIV protease inhibitors with St.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403580, 'Darunavir', 'Stiripentol', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 and/or 3A4 may increase the plasma concentrations of stiripentol, which has been shown in vitro to undergo phase I metabolism via these pathways. Conversely, many of these inhibitors are also substrates of CYP450 1A2 and/or 3A4, and coadministration with stiripentol may alter their plasma concentrations as well. Stiripentol is both an inhibitor and inducer of CYP450 1A2 and 3A4 in vitro.', 'DDInter', 1767317476, 'Caution is advised when stiripentol is used with inhibitors of CYP450 1A2 and/or 3A4 that are also substrates of these enzymatic pathways. Patients should be monitored for potentially increased adverse effects of stiripentol such as anorexia, nausea, vomiting, weight loss, somnolence, dizziness, confusion, difficulty concentrating, ataxia, hypotonia, tremor, hyperkinesia, dysarthria, suicidal ideation/behavior, neutropenia, and thrombocytopenia. In addition, clinical and laboratory monitoring may be appropriate for concomitant drugs whenever stiripentol is added to or withdrawn from therapy. Dosage adjustments or alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'Caution is advised when stiripentol is used with inhibitors of CYP450 1A2 and/or 3A4 that are also substrates of these enzymatic pathways.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403581, 'Darunavir', 'Sufentanil', 'Moderate', 'Coadministration with darunavir may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by darunavir.', 'DDInter', 1767317476, 'Caution is advised if darunavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever darunavir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if darunavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403582, 'Darunavir', 'Sulfinpyrazone', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen. Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended. Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403583, 'Darunavir', 'Suvorexant', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of suvorexant, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'The recommended dose of suvorexant is 5 mg when used with moderate CYP450 3A4 inhibitors, and generally should not exceed 10 mg.', 'Metabolism', 'The recommended dose of suvorexant is 5 mg when used with moderate CYP450 3A4 inhibitors, and generally should not exceed 10 mg.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403584, 'Darunavir', 'Tacrolimus', 'Major', 'Coadministration with protease inhibitors (PIs) may significantly increase the blood concentrations of tacrolimus. The proposed mechanism is PI inhibition of intestinal and hepatic CYP450 3A4, the isoenzyme responsible for the metabolic clearance of tacrolimus. Enhanced tacrolimus oral bioavailability due to inhibition of intestinal P-glycoprotein (P-gp) efflux transporter may also contribute.', 'DDInter', 1767317476, 'Caution is advised when tacrolimus is used with protease inhibitors (PIs). Dosage reduction and/or prolongation of the dosing interval for tacrolimus will likely be required. Tacrolimus blood levels and renal function should be checked frequently and the dosage adjusted accordingly, particularly following initiation or discontinuation of PI therapy. Patients should be closely monitored for development of serious adverse effects such as nephrotoxicity, lymphoma and other malignancies, infections, diabetes, neurotoxicity (tremor, paraesthesia, encephalopathy, delirium, coma), hyperkalemia, QT prolongation, myocardial hypertrophy, and hypertension. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', 'Caution is advised when tacrolimus is used with protease inhibitors (PIs).', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403585, 'Darunavir', 'Tadalafil', 'Moderate', 'Coadministration with protease inhibitors (PIs) may increase the plasma concentrations of tadalafil. The mechanism is PI inhibition of tadalafil metabolism via CYP450 3A4. Although not specifically studied, other HIV protease inhibitors are expected to increase tadalafil exposure.', 'DDInter', 1767317476, 'Caution is advised if tadalafil is administered in combination with protease inhibitors. For the treatment of erectile dysfunction, the dosage of tadalafil should not exceed 10 mg once every 72 hours when used on an as-needed basis in patients receiving PI therapy. When given for once-daily use in the treatment of erectile dysfunction or benign prostatic hyperplasia, the maximum recommended dose is 2.5 mg. For the treatment of pulmonary arterial hypertension, tadalafil should be started at 20 mg once daily in patients who have been receiving ritonavir-boosted PI therapy for at least one week. The dosage may be increased to 40 mg once daily based upon individual tolerability. The use of tadalafil for PAH should be avoided during the initiation of ritonavir-boosted PI therapy. The manufacturers recommend that tadalafil be discontinued at least 24 hours prior to initiating the PIs. After at least one week, tadalafil may be resumed at 20 mg once daily, and the dosage increased to 40 mg once daily based upon individual tolerability. There is no need to discontinue tadalafil when initiating PI therapy that does not contain ritonavir as a pharmacokinetic booster, nor wait at least one week after PI therapy to start tadalafil. The tadalafil dosage should be initiated at or adjusted to 20 mg once daily when coadministered with nonritonavir-boosted PI regimens, then increased to 40 mg as tolerated. All patients treated with tadalafil should be advised to promptly notify their physician if they experience pain or tightness in the chest or jaw, irregular heartbeat, nausea, shortness of breath, visual disturbances, syncope, or prolonged erection (greater than 4 hours).', 'Metabolism', 'Caution is advised if tadalafil is administered in combination with protease inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403586, 'Darunavir', 'Talazoparib', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of talazoparib, which has been shown in vitro to be a substrate of the efflux transporter.', 'DDInter', 1767317476, 'No initial dosage adjustment is recommended by the manufacturer when talazoparib is coadministered with inhibitors of P-gp other than amiodarone, carvedilol, clarithromycin, itraconazole, or verapamil. However, patients should be closely monitored for adverse effects such as myelosuppression and myelodysplastic syndrome/acute myeloid leukemia, and dosage adjustments made or treatment withheld as needed in accordance with the product labeling.', 'Absorption', 'No initial dosage adjustment is recommended by the manufacturer when talazoparib is coadministered with inhibitors of P-gp other than amiodarone, carvedilol, clarithromycin, itraconazole, or verapamil.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403587, 'Darunavir', 'Tamoxifen', 'Moderate', 'Coadministration with darunavir may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by darunavir.', 'DDInter', 1767317476, 'Caution is advised if darunavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever darunavir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if darunavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403588, 'Darunavir', 'Tamsulosin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or 2D6 may increase the plasma concentrations of tamsulosin, which is primarily metabolized in the liver by these isoenzymes.', 'DDInter', 1767317476, 'Caution is advised if tamsulosin is used concomitantly with moderate CYP450 3A4 inhibitors (e.g., amiodarone, aprepitant, diltiazem, dronedarone, erythromycin, fluconazole, fluvoxamine, fusidic acid, imatinib, isavuconazonium, verapamil) and/or moderate to potent CYP450 2D6 inhibitors (e.g., abiraterone, bupropion, celecoxib, cinacalcet, darifenacin, dronedarone, duloxetine, fluoxetine, lorcaserin, paroxetine, propafenone, quinidine, ranolazine, rolapitant, terbinafine), particularly at a dosage higher than 0.4 mg/day. The potential for increased risk of adverse effects such as postural hypotension, syncope, and priapism should be considered.', 'Metabolism', 'Caution is advised if tamsulosin is used concomitantly with moderate CYP450 3A4 inhibitors (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403589, 'Darunavir', 'Tasimelteon', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 and/or 3A4 may increase the plasma concentrations of tasimelteon, which is primarily metabolized by these isoenzymes.', 'DDInter', 1767317476, 'Caution is advised if tasimelteon is prescribed in combination with inhibitors of CYP450 1A2 and/or 3A4. Patients should be monitored for excessive sedation and other side effects.', 'Metabolism', 'Caution is advised if tasimelteon is prescribed in combination with inhibitors of CYP450 1A2 and/or 3A4.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403590, 'Darunavir', 'Tazemetostat', 'Major', 'Coadministration with moderate or potent CYP450 3A4 inhibitors may significantly increase the plasma concentrations of tazemetostat, which is primarily metabolized by the isoenzyme. Clinically, this interaction may result in an increased risk of the frequency or severity of adverse reactions due to tazemetostat such as secondary malignancies, hemorrhage, pleural effusion, skin infection, dyspnea, pain, and respiratory distress.', 'DDInter', 1767317476, 'Concomitant use of tazemetostat with moderate or potent CYP450 3A4 inhibitors should generally be avoided. Alternative therapeutic agents with less inhibitory potential should be considered whenever possible. If coadministration with a moderate CYP450 3A4 inhibitor is required and no other alternatives are possible, the dose of tazemetostat should be reduced as per manufacturer recommendations. After the inhibitor has been discontinued for 3 elimination half-lives, the tazemetostat dose that was taken prior to initiating the inhibitor may be resumed. Patients should be advised to notify their health care professional if they experience signs or symptoms of possible tazemetostat-related adverse effects including secondary malignancies, hemorrhage, pleural effusion, skin infection, dyspnea, pain, and respiratory distress.', 'Metabolism', 'Concomitant use of tazemetostat with moderate or potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403591, 'Darunavir', 'Telaprevir', 'Moderate', 'Coadministration of telaprevir and darunavir/ritonavir may decrease the plasma concentrations of both telaprevir and darunavir. The mechanism of interaction has not been described.', 'DDInter', 1767317476, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic drug levels, concomitant use of telaprevir and darunavir/ritonavir should generally be avoided.', 'Others', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic drug levels, concomitant use of telaprevir and darunavir/ritonavir should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403592, 'Darunavir', 'Telotristat ethyl', 'Moderate', 'Coadministration with telotristat ethyl may decrease the plasma concentrations of drugs that are substrates of CYP450 3A4 and/or 2B6 isoenzymes. The proposed mechanism is accelerated clearance due to induction of CYP450 3A4 and/or 2B6 (in vitro) isoenzymes by telotristat ethyl.', 'DDInter', 1767317476, 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs, particularly those with a narrow therapeutic range, whenever telotristat ethyl is added to or withdrawn from therapy.', 'Metabolism', 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403593, 'Darunavir', 'Temsirolimus', 'Moderate', 'Coadministration of temsirolimus with inhibitors of CYP450 3A4 may increase the plasma concentrations of sirolimus, a major active metabolite of temsirolimus and known substrate of CYP450 3A4.', 'DDInter', 1767317476, 'Caution is advised if temsirolimus is prescribed in combination with CYP450 3A4 inhibitors. Pharmacologic response to temsirolimus should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the temsirolimus dosage adjusted as necessary.', 'Metabolism', 'Caution is advised if temsirolimus is prescribed in combination with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403594, 'Darunavir', 'Teniposide', 'Moderate', 'Coadministration with darunavir may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by darunavir.', 'DDInter', 1767317476, 'Caution is advised if darunavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever darunavir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if darunavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403595, 'Darunavir', 'Terfenadine', 'Major', 'Coadministration with protease inhibitors (PIs), particularly ritonavir, may significantly increase the plasma concentrations of astemizole and terfenadine. The mechanism is PI inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of both astemizole and terfenadine. Although the interaction has not been specifically studied with any of the available PIs, high plasma levels of astemizole and terfenadine (e.g., due to overdose or interaction with other potent 3A4 inhibitors such as macrolide antibiotics and azole antifungal agents) have been associated with prolongation of the QT interval on the ECG; ventricular arrhythmias including ventricular tachycardia, ventricular fibrillation, and torsade de pointes; cardiac arrest; and sudden death.', 'DDInter', 1767317476, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of astemizole and terfenadine, use of these agents in patients treated with protease inhibitors is considered contraindicated. Loratadine, cetirizine, or fexofenadine may be safer alternatives during therapy with PIs.', 'Metabolism', 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of astemizole and terfenadine, use of these agents in patients treated with protease inhibitors is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403596, 'Darunavir', 'Teriflunomide', 'Major', 'The recent, concomitant, or subsequent use (without the recommended leflunomide washout period or procedure) of other agents known to induce hepatotoxicity may potentiate the risk of liver injury associated with leflunomide. The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide.', 'DDInter', 1767317476, 'Caution is advised if leflunomide or teriflunomide must be used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice), and vice versa. Liver enzymes and bilirubin should be measured prior to initiation of leflunomide/teriflunomide therapy and at least monthly for the first six months of treatment and every 6 to 8 weeks thereafter. Patients with preexisting liver disease or elevated baseline liver enzymes (i.e., ALT greater than two times ULN) should not receive leflunomide or teriflunomide. Patients who develop elevated serum ALT greater than three times ULN while receiving these medications should discontinue treatment and be given washout procedures with cholestyramine or activated charcoal to accelerate elimination of leflunomide''s active metabolite from plasma, which otherwise may take up to two years. Follow-up monitoring should be conducted at least weekly until the ALT value is within normal range, and washout procedures repeated as necessary. All patients treated with leflunomide or teriflunomide should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'Caution is advised if leflunomide or teriflunomide must be used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403597, 'Darunavir', 'Testosterone (topical)', 'Moderate', 'Coadministration with darunavir may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by darunavir.', 'DDInter', 1767317476, 'Caution is advised if darunavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever darunavir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if darunavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403598, 'Darunavir', 'Tioguanine', 'Moderate', 'The concomitant or sequential use of other agents known to induce hepatotoxicity may potentiate the risk of liver injury associated with thioguanine. A high risk of liver toxicity characterized by vascular endothelial damage has been reported with long-term continuous use of thioguanine, particularly in children receiving the drug as part of maintenance therapy for acute lymphoblastic leukemia and in other conditions associated with continuous use.', 'DDInter', 1767317476, 'The risk of hepatic injury should be considered when thioguanine is used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Baseline and regular monitoring of hepatic function is recommended. Thioguanine therapy should be discontinued if there is evidence of toxic hepatitis or biliary stasis, as reversal of signs and symptoms of liver toxicity have been reported upon withdrawal. Early indications of liver toxicity are signs associated with portal hypertension such as thrombocytopenia out of proportion with neutropenia and splenomegaly. Elevations of liver enzymes have also been reported, but do not always occur.', 'Synergism', 'The risk of hepatic injury should be considered when thioguanine is used with other potentially hepatotoxic agents (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403599, 'Darunavir', 'Tiagabine', 'Moderate', 'Coadministration with darunavir may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by darunavir.', 'DDInter', 1767317476, 'Caution is advised if darunavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever darunavir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if darunavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403600, 'Darunavir', 'Ticagrelor', 'Moderate', 'Coadministration with moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of ticagrelor, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Although administration with moderate CYP450 3A4 inhibitors is thought to be generally safe, it may be advisable to monitor pharmacologic response to ticagrelor more closely following the addition and withdrawal of these agents. Discontinuation of the concomitant medication may be required if an interaction is suspected.', 'Metabolism', 'Although administration with moderate CYP450 3A4 inhibitors is thought to be generally safe, it may be advisable to monitor pharmacologic response to ticagrelor more closely following the addition and withdrawal of these agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403601, 'Darunavir', 'Tinidazole', 'Moderate', 'Coadministration with darunavir may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by darunavir.', 'DDInter', 1767317476, 'Caution is advised if darunavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever darunavir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if darunavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403602, 'Darunavir', 'Tipranavir', 'Major', 'Coadministration with tipranavir plus low-dose ritonavir may significantly decrease the plasma concentrations of other protease inhibitors. The mechanism of interaction has not been described. Data are available for amprenavir, atazanavir, lopinavir, and saquinavir.', 'DDInter', 1767317476, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, use of other protease inhibitors in combination with tipranavir/ritonavir is not recommended.', 'Others', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, use of other protease inhibitors in combination with tipranavir/ritonavir is not recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403603, 'Darunavir', 'Tofacitinib', 'Moderate', 'Coadministration with one or more medications that may result in both moderate inhibition of CYP450 3A4 and potent inhibition of CYP450 2C19 may significantly increase the plasma concentrations of tofacitinib, which is primarily metabolized by the former isoenzyme with minor contribution from the latter. Side effects including lymphopenia, neutropenia, anemia, serious infections, and hyperlipidemia may be increased.', 'DDInter', 1767317476, 'The dosage of tofacitinib should be reduced by 50% when used concomitantly with one or more medications that may result in both moderate inhibition of CYP450 3A4 and potent inhibition of CYP450 2C19. For example, the dose for patients receiving 10 mg twice daily should be reduced to 5 mg twice daily and the dose for patients receiving 5 mg twice daily should be reduced to 5 mg once daily. For patients receiving 11 mg once daily of the extended-release formulation, the dose should be reduced to 5 mg once daily of the immediate-release formulation. The dose for patients receiving 3.2 mg twice daily should be reduced to 3.2 mg once daily and the dose for patients receiving 4 mg twice daily should be reduced to 4 mg once daily. Moderate inhibitors of CYP450 3A4 include amiodarone, aprepitant, ciprofloxacin, crizotinib, darunavir, dalfopristin-quinupristin, diltiazem, dronedarone, erythromycin, fluconazole, fusidic acid, grapefruit juice, imatinib, isavuconazonium, netupitant, and verapamil. Potent inhibitors of CYP450 2C19 include fluconazole, fluvoxamine, esomeprazole, lansoprazole, and omeprazole. Inhibitors of CYP450 2C19 alone are unlikely to substantially alter the pharmacokinetics of tofacitinib.', 'Metabolism', 'The dosage of tofacitinib should be reduced by 50% when used concomitantly with one or more medications that may result in both moderate inhibition of CYP450 3A4 and potent inhibition of CYP450 2C19.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403604, 'Darunavir', 'Tolazamide', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 1767317476, 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Antagonism', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403605, 'Darunavir', 'Tolbutamide', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 1767317476, 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Antagonism', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403606, 'Darunavir', 'Tolterodine', 'Moderate', 'Coadministration with drugs that are inhibitors of CYP450 3A4 may increase the plasma concentrations of tolterodine, which is partially metabolized by the isoenzyme. The possibility of prolonged and/or increased pharmacologic effects of tolterodine should be considered. Although tolterodine is primarily metabolized by CYP450 2D6, there is some evidence that CYP450 3A4 may play a minor role, thus any alteration in its activity levels could conceivably affect the metabolism of tolterodine.', 'DDInter', 1767317476, 'The manufacturer recommends a maximum tolterodine dosage of 1 mg twice daily in patients receiving concomitant CYP450 3A4 inhibitors. Close clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. Patients should be advised to notify their physician if they experience an irregular heartbeat, severe blurry vision, difficulty urinating, dry mouth, headache, drowsiness, dizziness, or GI upset.', 'Metabolism', 'The manufacturer recommends a maximum tolterodine dosage of 1 mg twice daily in patients receiving concomitant CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403607, 'Darunavir', 'Toremifene', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of toremifene, which is primarily metabolized by the isoenzyme. Because toremifene is associated with dose- and concentration-dependent prolongation of the QT interval, increased levels may potentiate the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', 'DDInter', 1767317476, 'Clinical and laboratory monitoring for altered efficacy and safety of toremifene may be appropriate whenever a CYP450 3A inhibitor is added to or withdrawn from therapy. Complete blood counts, electrolyte levels (calcium, magnesium, potassium), and liver function tests should be obtained periodically. Patients should be advised to contact their physician if they experience vaginal bleeding or potential signs of blood clots such as chest pain, shortness of breath, sudden loss of vision, and pain, redness or swelling in an extremity. Patients should seek immediate medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Metabolism', 'Clinical and laboratory monitoring for altered efficacy and safety of toremifene may be appropriate whenever a CYP450 3A inhibitor is added to or withdrawn from therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403608, 'Darunavir', 'Trabectedin', 'Moderate', 'Coadministration of trabectedin with other agents known to induce hepatotoxicity may potentiate the risk of liver injury.', 'DDInter', 1767317476, 'The risk of hepatic injury should be considered when trabectedin is used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Monitoring of alkaline phosphatase, bilirubin, AST, and ALT should occur regularly during trabectedin treatment in accordance with the product labeling, or as often as necessary when clinical symptoms develop.', 'Synergism', 'The risk of hepatic injury should be considered when trabectedin is used with other potentially hepatotoxic agents (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403609, 'Darunavir', 'Trazodone', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations and pharmacologic effects of trazodone, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'If concomitant use cannot be avoided, a lower dosage of trazodone should be considered during coadministration with a potent CYP450 3A4 inhibitor. Pharmacologic response to trazodone should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the trazodone dosage adjusted as necessary. Patients should seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism', 'If concomitant use cannot be avoided, a lower dosage of trazodone should be considered during coadministration with a potent CYP450 3A4 inhibitor.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403610, 'Darunavir', 'Tretinoin', 'Moderate', 'Coadministration with darunavir may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by darunavir.', 'DDInter', 1767317476, 'Caution is advised if darunavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever darunavir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if darunavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403611, 'Darunavir', 'Triamcinolone (nasal)', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations and pharmacologic effects of corticosteroids, which are primarily metabolized by the isoenzyme. The interaction has been reported with potent inhibitors such as clarithromycin, erythromycin, itraconazole, nefazodone, cobicistat, and ritonavir during concomitant use of various corticosteroids, including inhaled, nasal, and ophthalmic formulations. Systemic corticosteroid adverse effects may occur following intensive or long-term continuous ophthalmic corticosteroid therapy. Cushing''s syndrome and adrenal insufficiency have been attributed to the interaction.', 'DDInter', 1767317476, 'The possibility of increased corticosteroid effects should be considered during coadministration with potent and moderate CYP450 3A4 inhibitors. When indicated for intranasal or inhalational use, alternative corticosteroids such as beclomethasone, which is less dependent on CYP450 3A4 metabolism, should be considered, particularly if long term treatment is required. Patients should be monitored for signs and symptoms of hypercorticism such as acne, striae, thinning of the skin, easy bruising, moon facies, dorsocervical "buffalo" hump, truncal obesity, increased appetite, acute weight gain, edema, hypertension, hirsutism, hyperhidrosis, proximal muscle wasting and weakness, glucose intolerance, exacerbation of preexisting diabetes, depression, and menstrual disorders. Other systemic glucocorticoid effects may include adrenal suppression, immunosuppression, posterior subcapsular cataracts, glaucoma, bone loss, and growth retardation in children and adolescents. Following extensive use with a potent CYP450 3A4 inhibitor, a progressive dosage reduction may be required over a longer period if the corticosteroid is to be withdrawn from therapy, as there may be a significant risk of adrenal suppression.', 'Metabolism', 'The possibility of increased corticosteroid effects should be considered during coadministration with potent and moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403612, 'Darunavir', 'Triamcinolone (ophthalmic)', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations and pharmacologic effects of corticosteroids, which are primarily metabolized by the isoenzyme. The interaction has been reported with potent inhibitors such as clarithromycin, erythromycin, itraconazole, nefazodone, cobicistat, and ritonavir during concomitant use of various corticosteroids, including inhaled, nasal, and ophthalmic formulations. Systemic corticosteroid adverse effects may occur following intensive or long-term continuous ophthalmic corticosteroid therapy. Cushing''s syndrome and adrenal insufficiency have been attributed to the interaction.', 'DDInter', 1767317476, 'The possibility of increased corticosteroid effects should be considered during coadministration with potent and moderate CYP450 3A4 inhibitors. When indicated for intranasal or inhalational use, alternative corticosteroids such as beclomethasone, which is less dependent on CYP450 3A4 metabolism, should be considered, particularly if long term treatment is required. Patients should be monitored for signs and symptoms of hypercorticism such as acne, striae, thinning of the skin, easy bruising, moon facies, dorsocervical "buffalo" hump, truncal obesity, increased appetite, acute weight gain, edema, hypertension, hirsutism, hyperhidrosis, proximal muscle wasting and weakness, glucose intolerance, exacerbation of preexisting diabetes, depression, and menstrual disorders. Other systemic glucocorticoid effects may include adrenal suppression, immunosuppression, posterior subcapsular cataracts, glaucoma, bone loss, and growth retardation in children and adolescents. Following extensive use with a potent CYP450 3A4 inhibitor, a progressive dosage reduction may be required over a longer period if the corticosteroid is to be withdrawn from therapy, as there may be a significant risk of adrenal suppression.', 'Metabolism', 'The possibility of increased corticosteroid effects should be considered during coadministration with potent and moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403613, 'Darunavir', 'Triazolam', 'Major', 'Coadministration with protease inhibitors (PIs) may significantly increase the plasma concentrations and pharmacologic effects of orally administered midazolam and triazolam. The mechanism is PI inhibition of intestinal and hepatic CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of these benzodiazepines. Within the class, ritonavir is the most potent CYP450 3A4 inhibitor, while saquinavir is considered one of the weaker ones.', 'DDInter', 1767317476, 'Given the potential for prolonged and/or increased sedation and respiratory depression associated with excessive benzodiazepine blood levels, concomitant use of oral midazolam or triazolam with protease inhibitors is considered contraindicated. Caution and close clinical monitoring are recommended when administering parenteral midazolam in combination with protease inhibitors. Appropriate medical management should be readily available in case of respiratory depression and/or prolonged sedation. Dosage adjustment for midazolam may be appropriate, especially if more than a single dose of midazolam is administered.', 'Metabolism', 'Given the potential for prolonged and/or increased sedation and respiratory depression associated with excessive benzodiazepine blood levels, concomitant use of oral midazolam or triazolam with protease inhibitors is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403614, 'Darunavir', 'Troglitazone', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen. Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended. Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403615, 'Darunavir', 'Tucatinib', 'Moderate', 'Coadministration with tucatinib may increase the plasma concentrations of CYP450 3A4 substrates. The mechanism involves tucatinib-mediated inhibition of CYP450 3A isoenzymes.', 'DDInter', 1767317476, 'Caution is advised when tucatinib is used concomitantly with drugs that undergo metabolism by CYP450 3A4. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever tucatinib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when tucatinib is used concomitantly with drugs that undergo metabolism by CYP450 3A4.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403616, 'Darunavir', 'Ubrogepant', 'Moderate', 'Coadministration with moderate or weak inhibitors of CYP450 3A4 may increase the plasma concentrations of ubrogepant, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'The manufacturer recommends an initial ubrogepant dose of 50 mg when coadministered with moderate or weak CYP450 3A4 inhibitors. If needed, a second ubrogepant dose should be avoided within 24 hours of the initial dose when used with moderate CYP450 3A4 inhibitors.', 'Metabolism', 'The manufacturer recommends an initial ubrogepant dose of 50 mg when coadministered with moderate or weak CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403617, 'Darunavir', 'Ulipristal', 'Minor', 'Coadministration with CYP450 3A4 inhibitors may increase the plasma concentrations of ulipristal, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403618, 'Darunavir', 'Upadacitinib', 'Moderate', 'Coadministration with moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of upadacitinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Caution is recommended if upadacitinib is used with moderate CYP450 3A4 inhibitors. Pharmacologic effects of upadacitinib should be monitored more closely whenever a moderate CYP450 3A4 inhibitor is added to or withdrawn from therapy.', 'Metabolism', 'Caution is recommended if upadacitinib is used with moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403619, 'Darunavir', 'Valdecoxib', 'Moderate', 'Coadministration with darunavir may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by darunavir.', 'DDInter', 1767317476, 'Caution is advised if darunavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever darunavir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if darunavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403620, 'Darunavir', 'Vardenafil', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or 2C9 may increase the plasma concentrations of vardenafil, which is primarily metabolized by the former and secondarily by the latter. The possibility of prolonged and/or increased pharmacologic effects of vardenafil should be considered.', 'DDInter', 1767317476, 'Dosage adjustments may be appropriate for vardenafil whenever a CYP450 3A4 and/or 2C9 inhibitor is added to or withdrawn from therapy based on efficacy and patient tolerance. The manufacturer recommends that an initial vardenafil dosage of 2.5 to 5 mg be used in patients treated concomitantly with a potent CYP450 3A4 inhibitor such as erythromycin, itraconazole, ketoconazole, indinavir, and ritonavir.', 'Metabolism', 'Dosage adjustments may be appropriate for vardenafil whenever a CYP450 3A4 and/or 2C9 inhibitor is added to or withdrawn from therapy based on efficacy and patient tolerance.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403621, 'Darunavir', 'Vemurafenib', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen. Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended. Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403622, 'Darunavir', 'Venetoclax', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of venetoclax, which is a substrate of the isoenzyme.', 'DDInter', 1767317476, 'Concomitant use of venetoclax with moderate CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends that venetoclax dosage be reduced by at least 50%. The dosage used prior to initiating the CYP450 3A4 inhibitor may be resumed 2 to 3 days after discontinuation of the inhibitor.', 'Metabolism', 'Concomitant use of venetoclax with moderate CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403623, 'Darunavir', 'Venlafaxine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of venlafaxine and its metabolite, O-desmethylvenlafaxine (ODV). The possibility of prolonged and/or increased pharmacologic effects of venlafaxine should be considered. Although venlafaxine is primarily metabolized by CYP450 2D6, there is some evidence that CYP450 3A4 may play a minor role, thus any alteration in its activity levels could conceivably affect the metabolism of venlafaxine. The interaction may be of greater concern in patients who are CYP450 2D6-deficient, or so-called poor metabolizers of CYP450 2D6 (approximately 7% of Caucasians and less than 2% of Asians and individuals of African descent).', 'DDInter', 1767317476, 'Caution is recommended when venlafaxine is used with CYP450 3A4 inhibitors.', 'Metabolism', 'Caution is recommended when venlafaxine is used with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1403624, 'Darunavir', 'Vilazodone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of vilazodone, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'When used with moderate CYP450 3A4 inhibitors, the maintenance dosage of vilazodone should be reduced to 20 mg/day in patients who develop intolerable adverse events.', 'Metabolism', 'When used with moderate CYP450 3A4 inhibitors, the maintenance dosage of vilazodone should be reduced to 20 mg/day in patients who develop intolerable adverse events.', NULL, NULL, 'Moderate', 'pharmacodynamic');
