    ) VALUES (1403836, 'Dasatinib', 'Lomitapide', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of lomitapide, which is primarily metabolized by the isoenzyme.', NULL, 'The maximum recommended dosage of lomitapide is 30 mg daily when used in combination with weak CYP450 3A4 inhibitors such as alprazolam, amiodarone, amlodipine, atorvastatin, bicalutamide, cilostazol, cimetidine, cyclosporine, dasatinib, fluoxetine, fluvoxamine, ginkgo, goldenseal, isoniazid, ivacaftor, lapatinib, nilotinib, pazopanib, ranitidine, ranolazine, ticagrelor, tipranavir/ritonavir, and zileuton.', NULL, 'The maximum recommended dosage of lomitapide is 30 mg daily when used in combination with weak CYP450 3A4 inhibitors such as alprazolam, amiodarone, amlodipine, atorvastatin, bicalutamide, cilostazol, cimetidine, cyclosporine, dasatinib, fluoxetine, fluvoxamine, ginkgo, goldenseal, isoniazid, ivacaftor, lapatinib, nilotinib, pazopanib, ranitidine, ranolazine, ticagrelor, tipranavir/ritonavir, and zileuton.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
