INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (134498, 'Demeclocycline', 'Mannitol', 'Minor', 'The coadministration of diuretics and tetracyclines may result in decreased renal function manifested by increases in serum creatinine and blood urea nitrogen (BUN). Usually, no clinical intervention is necessary, unless decreases in renal function occur. If renal function deteriorates, discontinuation of one or both agents may be necessary.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/134655/', '[1] Jick H, Slone D, Shapiro S, et al "Tetracycline and drug-attributed rises in blood urea nitrogen: a report from the Boston Collaborative Drug Surveillance Program." JAMA 220 (1972): 377-9[2] Alexander MR "Tetracycline and rises in urea nitrogen." JAMA 221 (1972): 713-4[3] Garty M, Hurwitz A "Effect of cimetidine and antacids on gastrointestinal absorption of tetracycline." Clin Pharmacol Ther 28 (1980): 203-7[4] Tannenberg AM "Tetracycline and rises in urea nitrogen." JAMA 221 (1972): 713[5] "Product Information. Achromycin (tetracycline)." Lederle Laboratories, Wayne, NJ.[6] "Product Information. Declomycin (demeclocycline)." Lederle Laboratories, Wayne, NJ.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (134499, 'Digitoxin', 'Mannitol', 'Moderate', 'Although diuretics and digitalis glycosides are frequently and appropriately used together, diuretic-induced hypokalemia and hypomagnesemia may predispose patients on digitalis to arrhythmias.', NULL, 'Digoxin, potassium and magnesium levels should be followed closely.', NULL, 'Digoxin, potassium and magnesium levels should be followed closely. Hypokalemia and hypomagnesemia should be treated appropriately. Digitalis dose adjustments may be required. Patients should be advised to notify their physicians if they experience signs of possible digoxin toxicity or electrolyte disturbances, such as weakness, lethargy, muscle pains or cramps, nausea, anorexia, visual disturbances, or irregular heartbeats.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/134656/', '[1] Semple P, Tilstone WJ, Lawson DH "Furosemide and urinary digoxin clearance." N Engl J Med 293 (1975): 612-3[2] Tilstone WJ, Semple PF, Lawson DH, Boyle JA "Effects of furosemide on glomerular filtration rate and clearance of practolol, digoxin, cephaloridine, and gentamicin." Clin Pharmacol Ther 22 (1977): 389-94[3] Whang R, Oei TO, Watanabe A "Frequency of hypomagnesemia in hospitalized patients receiving digitalis." Arch Intern Med 145 (1985): 655-6[4] Macolic V, Vrhovac B "Pharmacokinetics and interactions of digoxin, theophylline and furosemide in diseases with edema." Int J Clin Pharmacol Ther Toxicol 31 (1993): 6-11[5] McAllister RG, Howell SM, Gomer MS, Selby JB "Effect of intravenous furosemide on the renal excretion of digoxin." J Clin Pharmacol 16 (1976): 110-7[6] Waldorff S, Hansen PB, Kjaergard H, Buch J, Egeblad H, Steiness E "Amiloride-induced changes in digoxin dynamics and kinetics: abolition of digoxin-induced inotropism with amiloride." Clin Pharmacol Ther 30 (1981): 172-6[7] Brown DD, Dormois JC, Abraham GN, et al "Effect of furosemide on the renal excretion of digoxin." Clin Pharmacol Ther 20 (1976): 395-400[8] Malcolm AD, Leung FY, Fuchs JC, Duarte JE "Digoxin kinetics during furosemide administration." Clin Pharmacol Ther 21 (1977): 567-74[9] Cohen L, Kitzes R "Magnesium Sulfate and digitalis-toxic arrhythmias." JAMA 249 (1983): 2808-10[10] Leary WP, Reyes AJ "Drug interactions with diuretics." S Afr Med J 65 (1984): 455-61', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (134500, 'Epoprostenol', 'Mannitol', 'Moderate', 'Coadministration of medicines targeting the prostacyclin pathway (e.g., prostacyclin (PGI2), PGI2 analogs, or selective non-prostanoid prostacyclin IP receptor agonists) with diuretics, antihypertensive agents, or other vasodilators may potentiate the hypotensive effects of these agents. The mechanism involves the additive reduction in blood pressure due to the vasodilatory effects of PGI2, PGI2 analogs or selective non-prostanoid prostacyclin IP receptor agonists on the prostacyclin pathway.', NULL, 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently.', NULL, 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently. If these drugs are used together, it is generally recommended that blood pressure be measured more frequently until a stable blood pressure pattern is observed. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostatic hypotension, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/134657/', '[1] "Product Information. Flolan (epoprostenol)." Glaxo Wellcome, Research Triangle Park, NC.[2] "Product Information. Remodulin (treprostinil)." United Therapeutics Corp, Silver Spring, MD.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Buchdahl RM, Taylor P, Warner JD "Nebulised ribavirin for adenovirus pneumonia." Lancet 2 (1985): 1070-1[5] "Product Information. Ventavis (iloprost)." Actelion Pharmaceuticals US Inc, South San Francisco, CA.[6] "Product Information. Xatral (alfuzosin)." Sanofi-Synthelabo Canada Inc, Markham, ON.[7] Cerner Multum, Inc. "Australian Product Information." O 0', 1767365207);
