INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (134503, 'Eslicarbazepine', 'Mannitol', 'Moderate', 'Coadministration with other medications that can lower serum sodium levels such as diuretics, selective serotonin reuptake inhibitors (SSRIs), and serotonin-noradrenaline reuptake inhibitors (SNRIs) may potentiate the risk of hyponatremia associated with eslicarbazepine.', NULL, 'Routine monitoring of serum sodium and chloride levels should be considered during maintenance treatment with eslicarbazepine acetate and concomitant use of other medications known to decrease serum sodium levels.', NULL, 'Routine monitoring of serum sodium and chloride levels should be considered during maintenance treatment with eslicarbazepine acetate and concomitant use of other medications known to decrease serum sodium levels. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hyponatremia such as nausea, vomiting, headache, malaise, lethargy, irritability, difficulty concentrating, memory impairment, confusion, weakness, muscle spasm, and unsteadiness (which may lead to falls). More severe and/or acute cases may include hallucination, syncope, seizure, coma, respiratory arrest, and death. Depending on the severity of hyponatremia, dosage reduction or discontinuation of eslicarbazepine acetate and other medications may be required, and appropriate medical intervention instituted as necessary.', 'Synergism', 'Sorbitol, Sulfamethizole, Sodium citrate, Magnesium citrate, Sodium bicarbonate, Sodium chloride, Chlorhexidine, Neomycin, Alvimopan, Sorbitol, Tegaserod, More', 'Brivaracetam, Topiramate, Ezogabine, Cenobamate, Lamotrigine, Methylphenobarbital, Ethosuximide, Lacosamide, Tiagabine, Oxcarbazepine, Ethotoin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/134660/', '[1] "Product Information. Aptiom (eslicarbazepine)." Sunovion Pharmaceuticals Inc, Marlborough, MA.', 1767369485);
