INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (134855, 'Xylometazoline (nasal)', 'Maprotiline', 'Moderate', 'Coadministration with tri- or tetra-cyclic antidepressants may theoretically potentiate the sympathomimetic effects (e.g., hypertension, tachycardia) of systemically absorbed xylometazoline following topical (i.e., nasal) administration. The proposed mechanism may involve additive or synergistic increases in circulating catecholamines, such as noradrenaline (norepinephrine).', NULL, 'Until further information is available, caution is advised if a tri- or tetra-cyclic antidepressant is used concomitantly with topical xylometazoline.', NULL, 'Until further information is available, caution is advised if a tri- or tetra-cyclic antidepressant is used concomitantly with topical xylometazoline. Blood pressure and heart rate should be monitored at regular intervals during concomitant therapy. A dose reduction or discontinuation of concomitant therapy should be considered in patients who experience a sustained increase in blood pressure or heart rate.', 'Synergism', 'Citalopram, Sertraline, Paroxetine, Fluoxetine, St. John''s Wort, Nefazodone, Oxitriptan, Viloxazine, Vortioxetine, Trazodone, Tryptophan, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/135012/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Agencia Espaola de Medicamentos y Productos Sanitarios Healthcare "Centro de informacin online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (134856, 'Yohimbine', 'Maprotiline', 'Moderate', 'Some antidepressants such as the tricyclics may potentiate the pressor effect of yohimbine. The mechanism is a synergistic sympathomimetic effect due to stimulation of norepinephrine release by yohimbine and inhibition of norepinephrine reuptake by certain antidepressants.', NULL, 'Yohimbine should be avoided in patients undergoing concurrent treatment with tricyclic antidepressants or other drugs that interfere with neuronal uptake or metabolism of norepinephrine.', NULL, 'Yohimbine should be avoided in patients undergoing concurrent treatment with tricyclic antidepressants or other drugs that interfere with neuronal uptake or metabolism of norepinephrine.', 'Synergism', 'Citalopram, Sertraline, Fluoxetine, St. John''s Wort, Milnacipran, Vortioxetine, Levomilnacipran, Duloxetine, Viloxazine, Esketamine, Fluvoxamine, More', 'Pentosan polysulfate, Calcium chloride, Phenazopyridine, Sildenafil, Alprostadil, Acetohydroxamic acid, Avanafil, Phenyl salicylate, Tadalafil, Magnesium hydroxide, Ammonium chloride, Dimethyl sulfoxide', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/135013/', '[1] Mansoor GA "Herbs and alternative therapies in the hypertension clinic." Am J Hypertens 14(9 Pt 1) (2001): 971-5[2] Fugh-Berman A "Herb-drug interactions." Lancet 355 (2000): 134-8[3] "Product Information. Yocon (yohimbine)." Glenwood Inc, Tenafly, NJ.', 1767369485);
