INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (134911, 'Idelalisib', 'Maraviroc', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of maraviroc, which is primarily metabolized by the isoenzyme. Patients with severe renal impairment or end-stage renal disease (CrCl <30 mL/min) given maraviroc in combination with potent CYP450 3A4 inhibitors may have an increased risk of postural hypotension due to increased maraviroc exposure.', NULL, 'Maraviroc should be administered at a dosage of 150 mg twice daily during coadministration with potent CYP450 3A4 inhibitors, including most protease inhibitors except tipranavir plus ritonavir.', NULL, 'Maraviroc should be administered at a dosage of 150 mg twice daily during coadministration with potent CYP450 3A4 inhibitors, including most protease inhibitors except tipranavir plus ritonavir. This dosage is recommended whether or not a concomitant CYP450 3A4 inducer (e.g., efavirenz, rifampin, carbamazepine, phenobarbital, phenytoin) is given. Patients with moderate hepatic impairment who receive 150 mg with a potent CYP450 3A4 inhibitor should be monitored closely for maraviroc-associated adverse events. Maraviroc is contraindicated for use in combination with potent CYP450 3A4 inhibitors in patients with severe renal impairment or end-stage renal disease (CrCl <30 mL/min).', 'Metabolism', 'Cabotegravir, Simeprevir, Zanamivir, Valaciclovir, Dolutegravir, Oseltamivir, Acyclovir, Famciclovir, Ribavirin, Tecovirimat, Maribavir, More', 'Acalabrutinib, Dacomitinib, Avapritinib, Bosutinib, Selumetinib, Vandetanib, Cobimetinib, Afatinib, Tivozanib, Zanubrutinib, Axitinib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/135068/', '[1] "Product Information. Selzentry (maraviroc)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[4] "Product Information. Selzentry (maraviroc)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767369485);
