INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (134999, 'Telaprevir', 'Maraviroc', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of maraviroc, which is primarily metabolized by the isoenzyme. Patients with severe renal impairment or end-stage renal disease (CrCl <30 mL/min) given maraviroc in combination with potent CYP450 3A4 inhibitors may have an increased risk of postural hypotension due to increased maraviroc exposure.', NULL, 'Maraviroc should be administered at a dosage of 150 mg twice daily during coadministration with potent CYP450 3A4 inhibitors, including most protease inhibitors except tipranavir plus ritonavir.', NULL, 'Maraviroc should be administered at a dosage of 150 mg twice daily during coadministration with potent CYP450 3A4 inhibitors, including most protease inhibitors except tipranavir plus ritonavir. This dosage is recommended whether or not a concomitant CYP450 3A4 inducer (e.g., efavirenz, rifampin, carbamazepine, phenobarbital, phenytoin) is given. Patients with moderate hepatic impairment who receive 150 mg with a potent CYP450 3A4 inhibitor should be monitored closely for maraviroc-associated adverse events. Maraviroc is contraindicated for use in combination with potent CYP450 3A4 inhibitors in patients with severe renal impairment or end-stage renal disease (CrCl <30 mL/min).', 'Metabolism', 'Lamivudine, Glecaprevir, Cabotegravir, Zanamivir, Valaciclovir, Lenacapavir, Telaprevir, Velpatasvir, Rilpivirine, Oseltamivir, Cobicistat, More', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Rilpivirine, Dolutegravir, Oseltamivir, More', 'Major', NULL, 