INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1404541, 'Daunorubicin (liposomal)', 'Mesoridazine', 'Major', 'Mesoridazine can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Mesoridazine overdosage has been associated with ventricular arrhythmias and death. The extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s). In addition, certain agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) may have additive parasympatholytic and central nervous system-depressant effects when used in combination with mesoridazine. Excessive parasympatholytic effects may include paralytic ileus, hyperthermia, mydriasis, blurred vision, tachycardia, urinary retention, psychosis, and seizures.', 'DDInter', 1767317476, 'Coadministration of mesoridazine with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', 'Coadministration of mesoridazine with other drugs that can prolong the QT interval is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1404542, 'Daunorubicin (liposomal)', 'Orciprenaline', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clinically significant prolongation of QT interval and hypokalemia occur infrequently when beta-2 agonists are inhaled at normally recommended dosages. However, these effects may be more common when the drugs are administered systemically or when recommended dosages are exceeded.', 'DDInter', 1767317476, 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1404543, 'Daunorubicin (liposomal)', 'Methadone', 'Major', 'Methadone may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. High dosages of methadone alone have been associated with QT interval prolongation and torsade de pointes.', 'DDInter', 1767317476, 'Caution is recommended if methadone is used in combination with other drugs that can prolong the QT interval, particularly in the setting of chronic pain management or methadone maintenance for opioid dependency where high dosages may be employed. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. If taking drugs that also cause CNS-depressant or orthostatic effects (e.g., sedating antihistamines, psychotropic drugs like tricyclic antidepressants, phenothiazines, and neuroleptics), patients should be made aware of the possibility of additive effects with methadone and counseled to avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism', 'Caution is recommended if methadone is used in combination with other drugs that can prolong the QT interval, particularly in the setting of chronic pain management or methadone maintenance for opioid dependency where high dosages may be employed.', NULL, NULL, 'Major', 'pharmacodynamic');
