INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1404567, 'Daunorubicin (liposomal)', 'Olanzapine', 'Moderate', 'It is uncertain whether olanzapine causes clinically significant prolongation of the QT interval.', 'DDInter', 1767317476, 'Some authorities recommend caution when olanzapine is used with drugs that are known to cause QT prolongation.', 'Synergism', 'Some authorities recommend caution when olanzapine is used with drugs that are known to cause QT prolongation.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1404568, 'Daunorubicin (liposomal)', 'Olaparib', 'Moderate', 'Coadministration with other myelosuppressive agents may potentiate and prolong the bone marrow toxicity associated with olaparib. Myelodysplastic syndrome (MDS) and acute myeloid leukemia (AML) have been reported in patients treated with olaparib.', 'DDInter', 1767317476, 'Caution is advised if olaparib is prescribed with other myelosuppressive agents. Complete blood counts should be performed at baseline and monthly thereafter. Do not start olaparib until patients have recovered from hematologic toxicity caused by previous chemotherapy.', 'Synergism', 'Caution is advised if olaparib is prescribed with other myelosuppressive agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1404569, 'Daunorubicin (liposomal)', 'Olodaterol', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clinically significant prolongation of QT interval and hypokalemia occur infrequently when beta-2 agonists are inhaled at normally recommended dosages. However, these effects may be more common when the drugs are administered systemically or when recommended dosages are exceeded.', 'DDInter', 1767317476, 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1404570, 'Daunorubicin (liposomal)', 'Ondansetron', 'Moderate', 'Theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. The risk of an individual agent or a combination of these agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances.', 'DDInter', 1767317476, 'Caution and clinical monitoring are recommended. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution and clinical monitoring are recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1404571, 'Daunorubicin (liposomal)', 'Osilodrostat', 'Moderate', 'Osilodrostat can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317476, 'Caution is recommended if osilodrostat is used in combination with other drugs that can prolong the QT interval. An electrocardiogram and serum electrolyte levels should be obtained prior to initiating osilodrostat therapy and periodically thereafter. Correct hypokalemia and/or hypomagnesemia before starting treatment and as indicated during treatment, as they may be risk factors for ventricular arrhythmias.', 'Synergism', 'Caution is recommended if osilodrostat is used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1404572, 'Daunorubicin (liposomal)', 'Osimertinib', 'Major', 'Osimertinib may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317476, 'Caution is recommended if osimertinib is used in combination with other drugs that can prolong the QT interval. ECG and serum electrolytes, including potassium, magnesium and calcium, should be monitored before starting osimertinib therapy and periodically during treatment.', 'Synergism', 'Caution is recommended if osimertinib is used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Major', 'pharmacodynamic');
