INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (135111, 'Vincristine', 'Mephenytoin', 'Moderate', 'Cases have been reported in which patients receiving chemotherapy have experienced markedly reduced plasma phenytoin concentrations and seizures. Other hydantoins may interact with chemotherapy in a similar manner. The mechanism of this interaction has not been clearly established.', NULL, 'It may be necessary to increase phenytoin dosage during chemotherapy and to decrease it after or between courses of chemotherapy.', NULL, 'It may be necessary to increase phenytoin dosage during chemotherapy and to decrease it after or between courses of chemotherapy. Intravenous phenytoin may be less likely to interact with chemotherapy. Careful monitoring of patients for seizure activity is recommended, as is monitoring of plasma phenytoin levels.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/135268/', '[1] Dofferhoff AS, Berendsen HH "Decreased phenytoin level after carboplatin treatment." Am J Med 89 (1990): 247[2] Neef C, de Voogd-van der Straaten "An interaction between cytostatic and anticonvulsant drugs." Clin Pharmacol Ther 43 (1988): 372-5[3] Bollini P, Riva R, Albani F, Ida et al "Decreased phenytoin level during antineoplastic therapy: a case report." Epilepsia 24 (1983): 75-8[4] Fincham RW, Schottelius DD "Decreased phenytoin levels in antineoplastic therapy." Ther Drug Monit 1 (1979): 277-83[5] Sylvester RK, Lewis FB, Caldwell KC, Lobell M, Perri R, Sawchuk RA "Impaired phenytoin bioavailability secondary to cisplantin, vinblastine, and bleomycin." Ther Drug Monit 6 (1984): 302-5[6] Grossman SA, Sheidler VR, Gilbert MR "Decreased phenytoin levels in patients receiving chemotherapy." Am J Med 87 (1989): 505-10[7] Jarosinski PF, Moscow JA, Alexander MS, et al "Altered phenytoin clearance during intensive chemotherapy for acute lymphoblastic leukemia." J Pediatr 112 (1988): 996-9[8] "Product Information. Oncovin (vincristine)." Lilly, Eli and Company, Indianapolis, IN.[9] Cerner Multum, Inc. "Australian Product Information." O 0[10] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[11] Fleisher D, Sheth N, Kou JH "Phenytoin interaction with enteral feedings administered through nasogastric tubes." JPEN J Parenter Enteral Nutr 14 (1990): 513-6[12] Krueger KA, Garnett WR, Comstock TJ, Fitzsimmons WE, Karnes HT, Pellock JM "Effect of two administration schedules of an enteral nutrient formula on phenytoin bioavailability." Epilepsia 28 (1987): 706-12[13] Haley CJ, Nelson J "Phenytoin-enteral feeding interaction." DICP 23 (1989): 796-8[14] Faraji B, Yu PP "Serum phenytoin levels of patients on gastrostomy tube feeding." J Neurosci Nurs 30 (1998): 55-9[15] Au Yeung SC, Ensom MH "Phenytoin and enteral feedings: does evidence support an interaction?" Ann Pharmacother 34 (2000): 896-905[16] Sandor P, Sellers EM, Dumbrell M, Khouw V "Effect of short- and long-term alcohol use on phenytoin kinetics in chronic alcoholics." Clin Pharmacol Ther 30 (1981): 390-7[17] Ozuna J, Friel P "Effect of enteral tube feeding on serum phenytoin levels." J Neurosurg Nurs 16 (1984): 289-91[18] Guidry JR, Eastwood TF, Curry SC "Phenytoin absorption in volunteers receiving selected enteral feedings." West J Med 150 (1989): 659-61[19] Marvel ME, Bertino JS "Comparative effects of an elemental and a complex enteral feeding formulation on the absorption of phenytoin suspension." JPEN J Parenter Enteral Nutr 15 (1991): 316-8[20] "Product Information. Dilantin (phenytoin)." Parke-Davis, Morris Plains, NJ.[21] Doak KK, Haas CE, Dunnigan KJ, et al. "Bioavailability of phenytoin acid and phenytoin sodium with enteral feedings." Pharmacotherapy 18 (1998): 637-45[22] Rodman DP, Stevenson TL, Ray TR "Phenytoin malabsorption after jejunostomy tube delivery." Pharmacotherapy 15 (1995): 801-5[23] Sellers EM, Holloway MR "Drug kinetics and alcohol ingestion." Clin Pharmacokinet 3 (1978): 440-52[24] Holtz L, Milton J, Sturek JK "Compatibility of medications with enteral feedings." JPEN J Parenter Enteral Nutr 11 (1987): 183-6', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (135112, 'Vincristine', 'Miconazole', 'Moderate', 'Coadministration with miconazole may increase the plasma concentrations of drugs that are substrates of CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by miconazole, which is thought to be a relatively potent inhibitor of the isoenzyme. Although systemic absorption following mucous membrane exposure is limited, the potential for interaction with drugs metabolized by CYP450 3A4 cannot be ruled out.', NULL, 'Caution is advised if miconazole must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if miconazole must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever miconazole is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/135269/', '[1] "Product Information. ORAVIG (miconazole)." Strativa Pharmaceuticals, a Division of Par Pharmaceuticals, Inc., Woodcliff Lake, NJ.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (135113, 'Vincristine', 'Midostaurin', 'Moderate', 'Coadministration with midostaurin may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of P-glycoprotein (P-gp), breast cancer resistance protein (BCRP), and/or organic anion transporting polypeptide (OATP) 1B1 transporters. The proposed mechanism, based on in vitro data, is decreased clearance due to midostaurin-mediated inhibition of P-gp, BCRP, and/or OATP1B1 transport proteins.', NULL, 'Caution is advised if midostaurin is used concomitantly with drugs that are substrates of P-gp, BCRP, and/or OATP1B1 transport proteins, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if midostaurin is used concomitantly with drugs that are substrates of P-gp, BCRP, and/or OATP1B1 transport proteins, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever midostaurin is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/135270/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Rydapt (midostaurin)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (135114, 'Vincristine', 'Mifepristone', 'Moderate', 'Based on in vitro inhibition data, coadministration with mifepristone may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by mifepristone.', NULL, 'Caution is advised if mifepristone must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if mifepristone must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever mifepristone is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/135271/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] He K, Woolf TF, Hollenberg PF "Mechanism-based inactivation of cytochrome P-450-3A4 by mifepristone (RU486)." J Pharmacol Exp Ther 288 (1999): 791-7[3] "Product Information. Mifeprex (mifepristone)" Danco Laboratories, New York, NY.[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[6] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[7] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[8] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[9] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[10] "Product Information. Mifeprex (mifepristone)" Danco Laboratories, New York, NY.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (135115, 'Vincristine', 'Mipomersen', 'Major', 'Coadministration of mipomersen with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Mipomersen can cause elevations in serum transaminases and hepatic steatosis.', NULL, 'Caution is advised if mipomersen is used with other potentially hepatotoxic agents (e.', NULL, 'Caution is advised if mipomersen is used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; amiodarone; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; kinase inhibitors; methotrexate; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; tamoxifen; tetracyclines; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; other lipid-lowering medications such as fenofibrate, lomitapide, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Mipomersen has not been studied with other LDL-lowering agents that can also increase hepatic fat, thus concomitant use is not recommended. Patients treated with mipomersen should have serum ALT, AST, alkaline phosphatase, and total bilirubin measured prior to initiation of treatment and regularly during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary. Since alcohol may increase levels of hepatic fat and induce or exacerbate liver injury, the manufacturer recommends that patients taking mipomersen not consume more than one alcoholic drink per day. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/135272/', '[1] "Product Information. Kynamro (mipomersen)." Genzyme Corporation, Cambridge, MA.[2] "Product Information. Kynamro (mipomersen)." Genzyme Corporation, Cambridge, MA.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (135116, 'Vincristine', 'Mitomycin', 'Moderate', 'Acute shortness of breath and severe bronchospasm have been reported following the administration of vinca alkaloids. These reactions have been encountered most frequently when the vinca alkaloid was used in combination with mitomycin-C. The exact mechanism of the interaction is not known. The reaction may within occur several minutes to 2 weeks after administration of the vinca alkaloid.', NULL, 'The acute shortness of breath and severe bronchospasm may require aggressive treatment (particularly if there is preexisting pulmonary dysfunction).', NULL, 'The acute shortness of breath and severe bronchospasm may require aggressive treatment (particularly if there is preexisting pulmonary dysfunction). Progressive dyspnea requiring chronic therapy may occur. The vinca alkaloid should not be readministered.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/135273/', '[1] Kris MG, Pablo D, Gralla RJ, Burke MT, Prestifillippo J, Lewin D "Dyspnea following vinblastine or vindesine administration in patients receiving mitomycin plus vinca alkaloid combination therapy." Cancer Treat Rep 68 (1984): 1029-31[2] Israel RH, Olson JP "Pulmonary edema associated with intravenous vinblastine." JAMA 240 (1978): 1585[3] Ozols RF, Hogan WM, Ostchega Y, Young RC "MVP (mitomycin, vinblastine, and progesterone): a second-line regimen in ovarian cancer with a high incidence of pulmonary toxicity." Cancer Treat Rep 67 (1983): 721-2[4] Dyke RW "Acute bronchospasm after a vinca alkaloid in patients previously treated with mitomycin." N Engl J Med 310 (1984): 389[5] Holzer KL, Harrison BR, Luedke SW, Luedke DW "Vinblastine-associated pulmonary toxicity in patients receiving combination therapy with mitomycin and cisplatin." Drug Intell Clin Pharm 20 (1986): 287-9[6] Rao SX, Ramaswamy G, Levin M, McCravey JW "Fatal acute respiratory failure after vinblastine-mitomycin therapy in lung carcinoma." Arch Intern Med 145 (1985): 1905-7[7] Konits PH, Aisner J, Sutherland JC, Wiernik PH "Possible pulmonary toxicity secondary to vinblastine." Cancer 50 (1982): 2771-4', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (135117, 'Vincristine', 'Mumps virus strain B level jeryl lynn live antigen', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/135274/', '[1] Fauci AS, Braunwald E, Isselbacher KJ, Wilson JD, Martin JB, Kasper DL, Hauser SL, Longo DL, eds. "Harrison''s Principles of Internal Medicine. 14th ed." New York, NY: McGraw-Hill Health Professionals Division (1998):[2] "Product Information. YF-Vax (yellow fever vaccine)." sanofi pasteur, Swiftwater, PA.[3] "Product Information. Attenuvax (measles virus vaccine, live, attenuated)." Merck & Co, Inc, West Point, PA.[4] Kriner P, Lopez K, Leung J, Harpaz R, Bialek SR "Notes from the field: varicella-associated death of a vaccinated child with leukemia - California, 2012." MMWR Morb Mortal Wkly Rep 63 (2014): 161[5] "Product Information. M-M-R II (measles/mumps/rubella virus vaccine)." Merck & Company Inc, West Point, PA.[6] Charkoudian LD, Kaiser GM, Steinmetz RL, Srivastava SK "Acute retinal necrosis after herpes zoster vaccination." Arch Ophthalmol 129 (2011): 1495-7[7] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993): 1-18[8] "Product Information. Meruvax II (rubella virus vaccine, live)." Merck & Co, Inc, West Point, PA.[9] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (135118, 'Vincristine', 'Nalidixic acid', 'Minor', 'Limited data suggest that chemotherapy with antineoplastic agents may reduce the plasma concentrations of oral quinolone antibiotics. The proposed mechanism is decreased quinolone absorption secondary to alteration of intestinal mucosa by cancer chemotherapy.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/135275/', '[1] Johnson EJ, MacGowan AP, Potter MN, et al "Reduced absorption of oral ciprofloxacin after chemotherapy for haematological malignancy." J Antimicrob Chemother 25 (1990): 837-42', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (135119, 'Vincristine', 'Natalizumab', 'Major', 'Concomitant or recent use of immunosuppressant, immunomodulating, or antineoplastic agents in patients treated with natalizumab may increase the risk of infections including progressive multifocal leukoencephalopathy (PML), a severely disabling, potentially fatal opportunistic viral infection of the brain.', NULL, 'The safety and efficacy of natalizumab in combination with immunosuppressant, immunomodulating, antineoplastic or other myelosuppressive agents have not been established.', NULL, 'The safety and efficacy of natalizumab in combination with immunosuppressant, immunomodulating, antineoplastic or other myelosuppressive agents have not been established. In general, patients receiving chronic therapy with such agents should not be treated with natalizumab due to potentially increased risk of PML and other serious infections. Typical symptoms associated with PML are diverse, progress over days to weeks, and include progressive weakness on one side of the body, clumsiness of limbs, disturbance of vision, and changes in thinking, memory, and orientation leading to confusion and personality changes. Due to the long half-life of natalizumab, immune effects are possible for up to 2 to 3 months following its discontinuation.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/135276/', '[1] "Product Information. Tysabri (natalizumab)." Elan Pharmaceutical/Athena Neurosciences Inc, South San Francisco, CA.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (135120, 'Vincristine', 'Naxitamab', 'Moderate', 'The risk of peripheral neuropathy may be increased during concurrent use of two or more agents that are associated with this adverse effect. Patient risk factors include diabetes and age older than 60 years.', NULL, 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet.', NULL, 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet. Since the development of peripheral neuropathy may be dose-related for many drugs, the recommended dosages should generally not be exceeded. Consideration should be given to dosage reduction or immediate discontinuation of these medications in patients who develop peripheral neuropathy to limit further damage.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/135277/', '[1] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[2] Pharmaceutical Society of Australia "APPGuide online. Australian prescription products guide online. Available from: URL: http://www.appco.com.au/appguide/default.asp." ([2006]):[3] Carrion C, Espinosa E, Herrero A, Garcia B "Possible vincristine-isoniazid interaction." Ann Pharmacother 29 (1995): 201[4] Argov Z, Mastaglia FL "Drug-induced peripheral neuropathies." Br Med J 1 (1979): 663-6', 1767365207);
