INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (135124, 'Vincristine', 'Neratinib', 'Moderate', 'Coadministration with neratinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine, liver, and/or kidney due to inhibition of P-glycoprotein-mediated drug efflux by neratinib.', NULL, 'Caution is advised when neratinib is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when neratinib is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever neratinib is added to or withdrawn from therapy.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/135281/', '[1] "Product Information. Nerlynx (neratinib)." Puma Biotechnology, Inc., Los Angeles, CA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Abbas R, Hug BA, Leister C, Burns J, Sonnichsen D "Pharmacokinetics of oral neratinib during co-administration of ketoconazole in healthy subjects." Br J Clin Pharmacol 71 (2011): 522-7[4] "Product Information. Nerlynx (neratinib)." Puma Biotechnology, Inc., Los Angeles, CA.', 1767365207);
