INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (135145, 'Vincristine', 'Peginterferon beta-1a', 'Moderate', 'Coadministration of beta interferons with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Use of beta interferons has been associated with rare cases of liver injury, including autoimmune hepatitis and severe liver damage leading to hepatic failure, some of which required transplantation.', NULL, 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.', NULL, 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; other interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/135302/', '[1] "Product Information. Plegridy (peginterferon beta-1a)." Biogen Idec Inc, Cambridge, MA.[2] "Product Information. Betaseron (interferon beta-1b)." Berlex, Richmond, CA.[3] "Product Information. Rebif (interferon beta-1a)." Serono Laboratories Inc, Norwell, MA.[4] "Product Information. Avonex (interferon beta-1a)." Biogen, Cambridge, MA.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (135146, 'Vincristine', 'Pexidartinib', 'Major', 'Serious cases of hepatotoxicity, some fatal, have occurred in patients treated with pexidartinib. Concomitant use of other potentially hepatotoxic agents may potentiate the risk of liver injury. The mechanism of hepatotoxicity is unknown, its occurrence cannot be predicted, and it is unknown whether liver injury occurs in the absence of increased transaminases.', NULL, 'The use of pexidartinib with other potentially hepatotoxic agents should be avoided.', NULL, 'The use of pexidartinib with other potentially hepatotoxic agents should be avoided. Patients treated with pexidartinib should have liver function tests, including AST, ALT, total bilirubin, direct bilirubin, ALP, and gamma-glutamyl transferase (GGT), prior to initiation of pexidartinib, weekly for the first 8 weeks, every 2 weeks for the next month, and every 3 months thereafter. Pexidartinib therapy may require a dosage reduction, to be withheld, or permanently discontinued based on the severity of the hepatotoxicity. A recurrence of increased serum transaminases, bilirubin, or ALP may occur upon rechallenge with a reduced dose of pexidartinib. Liver function tests should be performed weekly for the first month after rechallenge. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/135303/', '[1] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[2] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (135147, 'Vincristine', 'Phenytoin', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 and/or P-glycoprotein such as phenytoin may significantly decrease the plasma concentrations of vinca alkaloids, which are substrates of both the hepatic microsomal isoenzyme and intracellular efflux transporter. Cases have been reported in which patients receiving chemotherapy have experienced markedly reduced plasma phenytoin concentrations and seizures. One study showed that the absorption of phenytoin decreased significantly during chemotherapy. Intravenous phenytoin may be less likely to interact.', NULL, 'The use of vinca alkaloids in combination with potent CYP450 3A4 and/or P-glycoprotein inducers such as phenytoin should generally be avoided.', NULL, 'The use of vinca alkaloids in combination with potent CYP450 3A4 and/or P-glycoprotein inducers such as phenytoin should generally be avoided.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/135304/', '[1] Jarosinski PF, Moscow JA, Alexander MS, et al "Altered phenytoin clearance during intensive chemotherapy for acute lymphoblastic leukemia." J Pediatr 112 (1988): 996-9[2] "Product Information. Oncovin (vincristine)." Lilly, Eli and Company, Indianapolis, IN.[3] Sylvester RK, Lewis FB, Caldwell KC, Lobell M, Perri R, Sawchuk RA "Impaired phenytoin bioavailability secondary to cisplantin, vinblastine, and bleomycin." Ther Drug Monit 6 (1984): 302-5[4] Dofferhoff AS, Berendsen HH "Decreased phenytoin level after carboplatin treatment." Am J Med 89 (1990): 247[5] "Product Information. Marqibo (vinCRIStine liposome)." Talon Therapeutics Inc, South San Francisco,, CA.[6] Neef C, de Voogd-van der Straaten "An interaction between cytostatic and anticonvulsant drugs." Clin Pharmacol Ther 43 (1988): 372-5[7] Bollini P, Riva R, Albani F, Ida et al "Decreased phenytoin level during antineoplastic therapy: a case report." Epilepsia 24 (1983): 75-8[8] Grossman SA, Sheidler VR, Gilbert MR "Decreased phenytoin levels in patients receiving chemotherapy." Am J Med 87 (1989): 505-10[9] Fincham RW, Schottelius DD "Decreased phenytoin levels in antineoplastic therapy." Ther Drug Monit 1 (1979): 277-83', 1767365207);
