INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (135248, 'Mecamylamine', 'Ketamine', 'Moderate', 'Ketamine can cause hemodynamic instability such as arrhythmias and transient increases or decreases in blood pressure, heart rate, and cardiac index. Coadministration of ketamine with direct or indirect acting sympathomimetics or vasopressin may enhance the sympathomimetic effects of ketamine leading to additive increases in blood pressure and heart rate.', NULL, 'Close monitoring of vital signs, including blood pressure and heart rate is recommended whenever ketamine is used concomitantly with direct or indirect acting sympathomimetics or vasopressin.', NULL, 'Close monitoring of vital signs, including blood pressure and heart rate is recommended whenever ketamine is used concomitantly with direct or indirect acting sympathomimetics or vasopressin.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/135405/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Ketalar (ketamine)." JHP Pharmaceuticals, Saddle River, NJ.[4] Peltoniemi MA, Saari TI, Hagelberg NM, Laine K, Neuvonen PJ, Olkkola KT "S-ketamine concentrations are greatly increased by grapefruit juice." Eur J Clin Pharmacol 68 (2012): 979-86[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] "Product Information. Ketalar (ketamine)." JHP Pharmaceuticals, Saddle River, NJ.', 1767365207);
