INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1404812, 'Oxaprozin', 'Netilmicin', 'Moderate', 'The nephrotoxic effect of aminoglycosides may be potentiated by nonsteroidal anti-inflammatory drugs (NSAIDs), particularly if the latter had been given in high dosages for prolonged periods. Animal models suggest that renal prostaglandins may play a role in maintaining normal renal blood flow and glomerular filtration rate during the development of aminoglycoside nephrotoxicity, thus inhibition of their production by NSAIDs may worsen the renal damage. In premature infants, NSAIDs may increase the plasma concentrations of aminoglycosides. The proposed mechanism is decreased aminoglycoside clearance due to NSAID reduction of glomerular filtration rate.', 'DDInter', 1767317476, 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy. If concomitant administration is necessary, hydration status as well as renal and vestibular functions should be closely monitored.', 'Synergism', 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1404813, 'Oxaprozin', 'Nicardipine', 'Moderate', 'Limited data indicate that some cyclooxygenase inhibitors may attenuate the antihypertensive effects of some calcium channel blockers. The mechanism appears to be related to an alteration of vascular tone, which is dependent on prostacyclins and other vasodilatory prostanoids. When a nonsteroidal anti-inflammatory drug (NSAID) is added to the regimen of a patient who is already taking a calcium channel blocker, increased blood pressure may result. Also, the clinician should be aware that the risk of hypotension is increased when NSAIDs are withdrawn from the regimen.', 'DDInter', 1767317476, 'Monitoring for altered blood pressure control is recommended.', 'Antagonism', 'Monitoring for altered blood pressure control is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
