    ) VALUES (1405236, 'Deferasirox', 'Phenobarbital', 'Moderate', 'Coadministration with inducers of uridine diphosphate glucuronosyltransferase (UGT) may decrease the plasma concentrations of deferasirox, which is primarily metabolized by UGT1A1- and UGT1A3-mediated glucuronidation.', NULL, 'The concomitant use of deferasirox with potent UGT inducers such as rifampin, phenytoin, phenobarbital, carbamazepine, and ritonavir should generally be avoided.', NULL, 'The concomitant use of deferasirox with potent UGT inducers such as rifampin, phenytoin, phenobarbital, carbamazepine, and ritonavir should generally be avoided. Otherwise, consideration may be given to increasing the initial dosage of deferasirox by 50% during coadministration with potent UGT inducers, with subsequent adjustments made according to serum ferritin levels and clinical response.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
