INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (135693, 'Ribociclib', 'Methylprednisolone', 'Moderate', 'Coadministration with ribociclib may increase the plasma concentrations and pharmacologic effects of drugs that are substrates of CYP450 3A4. The proposed mechanism is decreased clearance due to ribociclib-mediated inhibition of CYP450 3A4 metabolism.', NULL, 'Caution is advised when ribociclib is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when ribociclib is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ribociclib is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/135850/', '[1] Rawden HC, Kokwaro GO, Ward SA, Edwards G "Relative contribution of cytochromes P-450 and flavin-containing monoxygenases to the metabolism of albendazole by human liver microsomes." Br J Clin Pharmacol 49 (2000): 313-22[2] Trivier JM, Libersa C, Belloc C, Lhermitte M "Amiodarone N-deethylation in human liver microsomes: involvement of cytochrome P450 3A enzymes (first report)." Life Sci 52 (1993): pl91-6[3] Kane GC, Lipsky JJ "Drug-grapefruit juice interactions." Mayo Clin Proc 75 (2000): 933-42[4] Katoh M, Nakajima M, Yamazaki H, Yokoi T "Inhibitory effects of CYP3A4 substrates and their metabolites on P-glycoprotein-mediated transport." Eur J Pharm Sci 12 (2001): 505-13[5] DSouza DL, Levasseur LM, Nezamis J, Robbins DK, Simms L, Koc