INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (135793, 'Fosphenytoin', 'Medroxyprogesterone acetate', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations and pharmacologic effects of medroxyprogesterone, which is primarily metabolized by the isoenzyme.', NULL, 'Pharmacologic response to medroxyprogesterone should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the dosage adjusted as necessary.', NULL, 'Pharmacologic response to medroxyprogesterone should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the dosage adjusted as necessary. When administered as the depot formulation for contraception, no dosage adjustment for medroxyprogesterone is currently recommended during coadministration with CYP450 3A4 inducers.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/135950/', '[1] Kobayashi K, Mimura N, Fujii H, et al "Role of human cytochrome P450 3A4 in metabolism of medroxyprogesterone acetate." Clin Cancer Res 6 (2000): 3297-303[2] Lundgren S, Lonning PE, Aakvaag A, Kvinnsland S, Lnning PE "Influence of aminoglutethimide on the metabolism of medroxyprogesterone acetate and megestrol acetate in postmenopausal patients with advanced breast cancer." Cancer Chemother Pharmacol 27 (1990): 101-5[3] "Product Information. Provera (medroxyprogesterone)." Pharmacia and Upjohn, Kalamazoo, MI.[4] "FFPRHC Guidance (April 2005). Drug interactions with hormonal contraception." J Fam Plann Reprod Health Care 31 (2005): 139-51[5] O''Brien MD, Guillebaud J "Contraception for women with epilepsy." Epilepsia