INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (136102, 'Fosphenytoin', 'Mefloquine', 'Moderate', 'Mefloquine may increase the risk of seizures and decrease the effectiveness of anticonvulsant drugs in patients with epilepsy. The mechanism is unknown, and it is unclear whether the seizures occur due to drug-drug or drug-disease interactions, as mefloquine alone and in combination with other related drugs (e.g., quinine, quinidine, chloroquine) have also been associated with seizures in some patients.', NULL, 'Because it can increase the risk of seizures in patients with epilepsy, mefloquine should only be used for curative treatment of malaria in such patients and only if there are compelling medical reasons for its use.', NULL, 'Because it can increase the risk of seizures in patients with epilepsy, mefloquine should only be used for curative treatment of malaria in such patients and only if there are compelling medical reasons for its use. Patient clinical status and anticonvulsant blood levels should be closely monitored if mefloquine is prescribed. Dosage adjustments may be required. Patients should be advised to notify their doctor if they experience a loss of seizure control. Mefloquine should not be prescribed for malaria prophylaxis in patients with a history of convulsions.', 'Others', 'Artesunate, Lumefantrine, Pyrimethamine, Tafenoquine, Atovaquone, Halofantrine, Primaquine, Proguanil', 'Fenfluramine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/136259/', '[1] Jimenez-Huete A, Gil-Nagel A, Franch O "Multifocal myoclonus associated with mefloquine chemoprophylaxis." Clin Neuropharmacol 25 (2002): 243[2] Pous E, Gascon J, Obach J, Corachan M "Mefloquine-induced grand mal seizure during malaria chemoprophylaxis in a non-epileptic subject." Trans R Soc Trop Med Hyg 89 (1995): 434[3] Singh K, Shanks GD, Wilde H "Seizures after mefloquine." Ann Intern Med 114 (1991): 994[4] Weinke T, Trautmann M, Held T, et al "Neuropsychiatric side effects after the use of mefloquine." Am J Trop Med Hyg 45 (1991): 86-91[5] Ries S, Pohlmanneden B "Seizures during malaria prophylaxis with mefloquine." Dtsch Med Wochenschr 118 (1993): 1911-2[6] Potasman I, Juven Y, Weller B, Schwartz E "Does mefloquine prophylaxis affect electroencephalographic patterns?" Am J Med 112 (2002): 147-9[7] "Product Information. Lariam (mefloquine)." Roche Laboratories, Nutley, NJ.[8] Ruff TA, Sherwen SJ, Donnan GA "Seizure associated with mefloquine for malaria prophylaxis." Med J Aust 161 (1994): 453[9] Bem JL, Kerr L, Stuerchler D "Mefloquine prophylaxis: an overview of spontaneous reports of severe psychiatric reactions and convulsions." J Trop Med Hyg 95 (1992): 167-79[10] "Product Information. Lariam (mefloquine)." Roche Laboratories, Nutley, NJ.[11] Schmidt LE, Dalhoff K "Food-drug interactions." Drugs 62 (2002): 1481-502[12] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[13] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[14] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[15] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (136103, 'Fostamatinib', 'Mefloquine', 'Moderate', 'Coadministration with fostamatinib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme and/or P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine and/or liver due to inhibition of CYP450 3A4-mediated metabolism and P-gp-mediated efflux by fostamatinib.', NULL, 'Caution is advised when fostamatinib is used concurrently with drugs that are known substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when fostamatinib is used concurrently with drugs that are known substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fostamatinib is added to or withdrawn from therapy.', 'Metabolism', 'Pyrimethamine, Tafenoquine, Atovaquone, Hydroxychloroquine, Primaquine, Proguanil, Sulfadoxine, Chloroquine', 'Eltrombopag, Romiplostim, Avatrombopag, Emicizumab, Coagulation factor VIIa Recombinant Human, Coagulation factor X human, Phylloquinone, Lusutrombopag, Anti-inhibitor coagulant complex, Antihemophilic factor, human recombinant, Fibrinogen human, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/136260/', '[1] "Product Information. Tavalisse (fostamatinib)." Rigel Pharmaceuticals, South San Francisco, CA.', 1767369485);
