INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (136108, 'Gemifloxacin', 'Mefloquine', 'Moderate', 'Certain quinolones, including gemifloxacin, may cause dose-related prolongation of the QT interval in some patients. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Although the risk of a serious interaction is probably low, caution is recommended if gemifloxacin is used in combination with other drugs that can prolong the QT interval.', NULL, 'Although the risk of a serious interaction is probably low, caution is recommended if gemifloxacin is used in combination with other drugs that can prolong the QT interval. Since the magnitude of QTc prolongation may increase with increasing plasma concentrations of gemifloxacin, the recommended dosage should not be exceeded, especially in patients with renal or hepatic impairment.', 'Synergism', 'Artesunate, Artemether, Pyrimethamine, Tafenoquine, Atovaquone, Proguanil, Sulfadoxine', 'Enoxacin, Nalidixic acid, Cinoxacin, Trovafloxacin, Delafloxacin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/136265/', '[1] Iannini PB, Doddamani S, Byazrova E, Curciumaru I, Kramer H "Risk of torsades de pointes with non-cardiac drugs." BMJ 322 (2001): 46-7[2] Ball P "Quinolone-induced QT interval prolongation: a not-so-unexpected class effect." J Antimicrob Chemother 45 (2000): 557-9[3] "Product Information. Factive (gemifloxacin)." GeneSoft Inc, San Francisco, CA.[4] Owens RC "Risk assessment for antimicrobial agent-induced QTc interval prolongation and torsades de pointes." Pharmacotherapy 21 (2001): 301-19[5] Kang J, Wang L, Chen XL, Triggle DJ, Rampe D "Interactions of a series of fluoroquinolone antibacterial drugs with the human cardiac K+ channel HERG." Mol Pharmacol 59 (2001): 122-6', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (136109, 'Gilteritinib', 'Mefloquine', 'Moderate', 'Gilteritinib may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution and clinical monitoring are recommended if gilteritinib is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution and clinical monitoring are recommended if gilteritinib is used in combination with other drugs that can prolong the QT interval. Patients should have electrocardiograms (ECGs) performed prior to initiation of treatment with gilteritinib, on days 8 and 15 of cycle 1, and prior to the start of the next two subsequent cycles. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Artemether, Pyrimethamine, Tafenoquine, Atovaquone, Proguanil, Sulfadoxine', 'Imatinib, Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, Tivozanib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/136266/', '[1] "Product Information. Xospata (gilteritinib)." Astellas Pharma US, Inc, Deerfield, IL.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (136110, 'Ginkgo biloba', 'Mefloquine', 'Moderate', 'Certain preparations of ginkgo biloba have been reported to induce seizures. There may be a theoretical risk of increased seizure potential when used with other agents that can lower the seizure threshold such as selective serotonin reuptake inhibitors (SSRI antidepressants or anorectics), monoamine oxidase inhibitors, neuroleptic agents, central nervous system stimulants, opioids, tricyclic antidepressants, other tricyclic compounds (e.g., cyclobenzaprine, phenothiazines), carbapenems, cholinergic agents, fluoroquinolones, interferons, chloroquine, mefloquine, lindane, and theophylline. Ginkgo products may contain varying amounts of 4''-O-methylpyridoxine (ginkgotoxin), a known neurotoxin found primarily in ginkgo biloba seeds but also detected in lesser amounts in the leaves. In vivo, 4''-O-methylpyridoxine competes with vitamin B6, which causes an indirect inhibition of glutamate decarboxylase and subsequent decrease in the formation of gamma-aminobutyric acid (GABA) in the brain. There have been published case reports of generalized convulsions and vomiting within several hours after ingestion of large amounts of ginkgo nuts/seeds, including in young children and healthy individuals with no known personal or family history of epilepsy.', NULL, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', NULL, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine. Because of inconsistencies in formulation and potency of commercial herbal preparations, there is no way to verify without laboratory testing if and in what quantity 4''-O-methylpyridoxine may be present in a given ginkgo preparation. Patients treated with agents that can lower the seizure threshold should preferably avoid the use of products containing ginkgo biloba.', 'Synergism', 'Artesunate, Lumefantrine, Artemether, Pyrimethamine, Tafenoquine, Atovaquone, Hydroxychloroquine, Halofantrine, Quinine, Primaquine, Proguanil, Sulfadoxine', 'Memantine, Aducanumab', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/136267/', '[1] Kajiyama Y, Fujii K, Takeuchi H, Manabe Y "Ginkgo seed poisoning." Pediatrics 109 (2002): 325-7[2] Granger AS "Ginkgo biloba precipitating epileptic seizures." Age Ageing 30 (2001): 523-5[3] Miller LG "Herbal medicinals: selected clinical considerations focusing on known or potential drug-herb interactions." Arch Intern Med 158 (1998): 2200-11[4] Gregory PJ "Seizure associated with Ginkgo biloba?." Ann Intern Med 134 (2001): 344[5] Harms SL, Garrard J, Schwinghammer P, Eberly LE, Chang Y, Leppik IE "Ginkgo biloba use in nursing home elderly with epilepsy or seizure disorder." Epilepsia 47 (2006): 323-9[6] Spinella M "Herbal medicines and epilepsy: the potential for benefit and adverse effects." Epilepsy Behav 2 (2001): 524-32[7] Miwa H, Iijima M, Tanaka S, Mizuno Y "Generalized convulsions after consuming a large amount of Gingko nuts." Epilepsia 42 (2001): 280-1[8] Kupiec T, Raj V "Fatal seizures due to potential herb-drug interactions with Ginkgo biloba." J Anal Toxicol 29 (2005): 755-8', 1767369485);
