INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (136193, 'Pitolisant', 'Mefloquine', 'Moderate', 'Limited data suggest that pitolisant may produce mild to moderate QT interval prolongation (10 to 13 milliseconds) at doses 3 to 6 times the standard therapeutic dose. Theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Concomitant use of pitolisant with other agents associated with QT interval prolongation should generally be avoided.', NULL, 'Concomitant use of pitolisant with other agents associated with QT interval prolongation should generally be avoided. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Artemether, Pyrimethamine, Tafenoquine, Atovaquone, Quinine, Proguanil, Sulfadoxine', 'Dextromethorphan, Dalfampridine, Sodium oxybate, Riluzole, Inotersen, Tafamidis', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/136350/', '[1] "Product Information. Wakix (pitolisant)." Harmony Biosciences, LLC, Plymouth Meeting, PA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Ingrezza (valbenazine)." Neurocrine Biosciences, Inc., San Diego, CA.', 1767369485);
