INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1405613, 'Icosapent', 'Defibrotide', 'Moderate', 'Omega-3 fatty acids (e.g., fish oil) may potentiate the pharmacologic effects of anticoagulants and other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, dextran, and nonsteroidal anti-inflammatory drugs (NSAIDs). The exact mechanism of interaction is unknown. Omega-3 fatty acids may possess mild antiplatelet and hypocoagulant activities.', 'DDInter', 1767317476, 'In general, patients should consult a healthcare provider before taking any herbal or nutritional supplements. Patients using omega-3 fatty acids in combination with anticoagulants or other drugs that affect hemostasis should be advised of the potential for increased risk of bleeding complications.', 'Synergism', 'In general, patients should consult a healthcare provider before taking any herbal or nutritional supplements.', NULL, NULL, 'Moderate', 'pharmacodynamic');
