INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1405701, 'Diclofenamide', 'Deflazacort', 'Moderate', 'The concomitant use of corticosteroids and agents that deplete potassium (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may result in increased risk of hypokalemia. Corticosteroids can produce hypokalemia and other electrolyte disturbances via mineralocorticoid effects, the degree of which varies with the agent and route of administration (i.e. systemic vs. local).', 'DDInter', 1767317476, 'Patients receiving potassium-depleting agents with corticosteroids should be monitored closely for development of hypokalemia, particularly if fludrocortisone or large doses of another corticosteroid or adrenocorticotropic agent is given. Potassium supplementation may be necessary. Patients should be advised to notify their physician if they experience signs of electrolyte disturbances such as weakness, lethargy, and muscle pains or cramps.', 'Synergism', 'Patients receiving potassium-depleting agents with corticosteroids should be monitored closely for development of hypokalemia, particularly if fludrocortisone or large doses of another corticosteroid or adrenocorticotropic agent is given.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1405702, 'Dicoumarol', 'Deflazacort', 'Moderate', 'Corticosteroids and adrenocorticotropic agents may alter the pharmacologic effects of oral anticoagulants. Both increased anticoagulant dosage requirements as well as bleeding and increased anticoagulant sensitivity have been reported during concomitant corticosteroid therapy. Proposed mechanisms include blood hypercoagulability or diminished vascular integrity induced by corticosteroids.', 'DDInter', 1767317476, 'Close monitoring for clinical and laboratory evidence of altered anticoagulant response is recommended during concurrent treatment with corticosteroids or adrenocorticotropic agents. Patients should be advised to promptly report any signs and symptoms of bleeding or blood clots.', 'Others', 'Close monitoring for clinical and laboratory evidence of altered anticoagulant response is recommended during concurrent treatment with corticosteroids or adrenocorticotropic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
